BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "addresses" (
	"id"	INTEGER NOT NULL,
	"latitude"	DECIMAL(10, 7),
	"longitude"	DECIMAL(10, 7),
	"country"	VARCHAR(255),
	"city"	VARCHAR(255),
	"street"	VARCHAR(255),
	"house"	VARCHAR(20),
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "alembic_version" (
	"version_num"	VARCHAR(32) NOT NULL,
	CONSTRAINT "alembic_version_pkc" PRIMARY KEY("version_num")
);
CREATE TABLE IF NOT EXISTS "auth_codes" (
	"id"	INTEGER NOT NULL,
	"user_id"	INTEGER NOT NULL,
	"code"	VARCHAR NOT NULL,
	"expires_at"	DATETIME NOT NULL,
	"created_at"	DATETIME,
	PRIMARY KEY("id"),
	FOREIGN KEY("user_id") REFERENCES "users"("id")
);
CREATE TABLE IF NOT EXISTS "beer_colors" (
	"id"	INTEGER NOT NULL,
	"name"	VARCHAR(50) NOT NULL,
	"srm"	DECIMAL(4, 2),
	"ebc"	DECIMAL(4, 2),
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "beers" (
	"id"	INTEGER NOT NULL,
	"name"	VARCHAR(45) NOT NULL,
	"brand"	VARCHAR(45) NOT NULL,
	"description"	VARCHAR(250),
	"photo"	VARCHAR(100),
	"alc_degree"	DECIMAL(2, 2) NOT NULL,
	"ibu"	INTEGER,
	"og"	DECIMAL(5, 3),
	"fg"	DECIMAL(5, 3),
	"barrel_aged"	BOOLEAN,
	"type_id"	INTEGER NOT NULL,
	"color_id"	INTEGER,
	PRIMARY KEY("id"),
	FOREIGN KEY("color_id") REFERENCES "beer_colors"("id"),
	FOREIGN KEY("type_id") REFERENCES "type_of_beers"("id")
);
CREATE TABLE IF NOT EXISTS "feedbacks" (
	"id"	INTEGER NOT NULL,
	"text"	TEXT,
	"ratings"	INTEGER NOT NULL,
	"user_id"	INTEGER NOT NULL,
	"beer_id"	INTEGER,
	"place_id"	INTEGER,
	"type_feedback"	VARCHAR(10) NOT NULL,
	"photo_url"	VARCHAR(255),
	PRIMARY KEY("id"),
	FOREIGN KEY("beer_id") REFERENCES "beers"("id"),
	FOREIGN KEY("place_id") REFERENCES "places"("id"),
	FOREIGN KEY("user_id") REFERENCES "users"("id"),
	CONSTRAINT "check_type_feedback" CHECK("type_feedback" IN ('beer', 'place'))
);
CREATE TABLE IF NOT EXISTS "geopositions" (
	"id"	INTEGER NOT NULL,
	"user_id"	INTEGER NOT NULL,
	"latitude"	DECIMAL(10, 7) NOT NULL,
	"longitude"	DECIMAL(10, 7) NOT NULL,
	"updated_at"	TIMESTAMP DEFAULT (CURRENT_TIMESTAMP),
	PRIMARY KEY("id"),
	FOREIGN KEY("user_id") REFERENCES "users"("id")
);
CREATE TABLE IF NOT EXISTS "levels" (
	"id"	INTEGER NOT NULL,
	"level_name"	VARCHAR(45) NOT NULL,
	"icon"	VARCHAR(200),
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "photos" (
	"id"	INTEGER NOT NULL,
	"photo_url"	VARCHAR(255),
	"place_id"	INTEGER,
	"feedback_id"	INTEGER,
	"beer_id"	INTEGER,
	PRIMARY KEY("id"),
	FOREIGN KEY("beer_id") REFERENCES "beers"("id"),
	FOREIGN KEY("feedback_id") REFERENCES "feedbacks"("id"),
	FOREIGN KEY("place_id") REFERENCES "places"("id")
);
CREATE TABLE IF NOT EXISTS "places" (
	"id"	INTEGER NOT NULL,
	"name"	VARCHAR(100),
	"address_id"	INTEGER NOT NULL,
	"type_place_id"	INTEGER NOT NULL,
	"phone_number"	VARCHAR(20),
	PRIMARY KEY("id"),
	FOREIGN KEY("address_id") REFERENCES "addresses"("id"),
	FOREIGN KEY("type_place_id") REFERENCES "type_of_places"("id")
);
CREATE TABLE IF NOT EXISTS "refresh_tokens" (
	"id"	INTEGER NOT NULL,
	"user_id"	INTEGER NOT NULL,
	"token"	VARCHAR NOT NULL,
	"expires_at"	DATETIME NOT NULL,
	"created_at"	DATETIME,
	PRIMARY KEY("id"),
	UNIQUE("token"),
	FOREIGN KEY("user_id") REFERENCES "users"("id")
);
CREATE TABLE IF NOT EXISTS "test" (
	"id"	INTEGER NOT NULL,
	"title"	VARCHAR,
	"description"	TEXT,
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "type_of_beers" (
	"id"	INTEGER NOT NULL,
	"name"	VARCHAR(45),
	"description"	VARCHAR(200),
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "type_of_places" (
	"id"	INTEGER NOT NULL,
	"name"	VARCHAR(50) NOT NULL,
	"description"	VARCHAR(200),
	PRIMARY KEY("id")
);
CREATE TABLE IF NOT EXISTS "user_friends" (
	"user_id"	INTEGER NOT NULL,
	"friend_id"	INTEGER NOT NULL,
	PRIMARY KEY("user_id","friend_id"),
	FOREIGN KEY("friend_id") REFERENCES "users"("id"),
	FOREIGN KEY("user_id") REFERENCES "users"("id")
);
CREATE TABLE IF NOT EXISTS "users" (
	"id"	INTEGER NOT NULL,
	"level_id"	INTEGER NOT NULL,
	"username"	VARCHAR NOT NULL,
	"email"	VARCHAR,
	"avatar_url"	VARCHAR,
	"register_at"	DATETIME,
	"phone_number"	VARCHAR,
	PRIMARY KEY("id"),
	FOREIGN KEY("level_id") REFERENCES "levels"("id")
);

Мы впервые разработали это пиво в 2007 году. Нашей первой попыткой партии пива на 2-й улице был Балтийский портер. То, что мы в итоге получили, стало известно как «Shake Down Nut Brown». Вы можете догадаться, что пошло не так… Но так много всего правильного! С каждым годом пиво становилось немного крепче, по вкусу и алкоголю. 

Мы используем черный солод для темного насыщенного цвета и дополнительный специальный солод для ароматов сухофруктов. Балтийские портеры ферментируются в холодном виде с использованием лагерных дрожжей. Это долгое медленное созревание создает мягкий вкусовой профиль. Ожидание выдержки в бочке из-под бурбона — самая захватывающая часть этого процесса пивоварения. Мы хотим получить этот дубовый характер с оттенками бурбона, для этого результата мы используем бочки 3-го цикла. Каждая партия немного отличалась от предыдущей.','https://brewerydb-images.s3.amazonaws.com/beer/tmEthz/upload_3Jl1St-medium.png',7,35,1.072,1.016,0,2,1);

Сорт сухого, свежего, фруктового, освежающего, не совсем бледного эля. № 9 действительно невозможно описать, потому что никогда не было ничего похожего на него. Наш секретный ингредиент представляет собой самый необычный аромат, который сбалансирован остаточной сладостью.','https://brewerydb-images.s3.amazonaws.com/beer/hB0QeO/upload_tI37PC-medium.png',5.1,30,1.044,1.008,0,12,1);

Сделано с добавлением лемонграсса.','https://brewerydb-images.s3.amazonaws.com/beer/5qG8kT/upload_o7PK06-medium.png',4.3,5,1.03,1.006,0,2,1);

Хватит уже каламбуров про яблоки! 

Мичиганский яблочный сидр сочетается с медом местного производства, финиками, сахаром демерара из Гайаны, сахаром мусковадо с Маврикия, специями и ароматизаторами Грэма.','https://brewerydb-images.s3.amazonaws.com/beer/KZPL21/upload_SQjelk-medium.png',14,NULL,NULL,NULL,0,19,1);

В знак уважения мы представляем вам эль в стиле Кельн "#KÖLSCH". Мы начинаем с того, что очищаем нашу воду до самого необходимого, а затем восстанавливаем ее, чтобы она соответствовала минеральному составу воды в Кельне, Германия. Затем мы добавляем тонны немецкого солода Pilsner и немного немецкого хмеля Perle и Tettnang. Легко пьется, бодрит и освежает!','https://brewerydb-images.s3.amazonaws.com/beer/m8f62Y/upload_IHiHKI-medium.png',4.8,18,1.042,1.006,0,20,1);
Светло-золотистого цвета, #Merica! — это американский лагер старой школы до сухого закона, который был сварен в 2015 году для ежегодной вечеринки в честь Дня независимости, устроенной панк-группой Dillinger Four из Миннеаполиса. Один из немногих стилей пива, действительно рожденных в США, американский лагер до сухого закона использовал кукурузу как неотъемлемую часть вкусового профиля, а не как простую замену ячменному солоду. Кукурузные хлопья, используемые в #Merica!, ощущаются как тонкий, свежий кукурузный вкус, в то время как мягкие хмелевые ароматы исходят от хмеля Warrior и Willamette, который добавляет мягкий фруктовый, травянистый характер, который остается на заднем плане. #Merica! идеально подходит для жаркого летнего дня и празднования дня рождения страны и 21-летия Dillinger Four.','https://brewerydb-images.s3.amazonaws.com/beer/R61vnh/upload_Pz78yV-medium.png',5,5,1.04,1.006,0,14,1);
Легенда гласит, что после того, как выпьет toeback quattro, один из ''Wildemannen'' выносит церковь Св. Екатерины в Хогстратене на прогулку. Он может нести на спине 105-метровую башню весом 100 тонн! Волшебное пиво, этот ''toeback''!','https://brewerydb-images.s3.amazonaws.com/beer/EPLPz5/upload_WLs95w-medium.png',9.5,20,1.064,1.012,0,10,1);

(512) FOUR — классический крепкий эль в английском стиле. Бисквитный солод Maris Otter и карамельный солод Crystal привносят ноты поджаренного хлеба, ириски и сухофруктов, которые идеально сбалансированы смесью аутентичных хмелей UK Fuggle и East Kent Golding. Охмеленный насухо Northdown, (512) FOUR — сложный и приятный эль.','https://brewerydb-images.s3.amazonaws.com/beer/oJFZwK/upload_B40pzO-medium.png',7.5,30,1.06,1.014,0,23,1);

В мифологии ацтеков 5 Lizard (Macuilcuetzpalin) является одним из 5 богов излишеств (Ahuiateteo) и символизирует излишества и чревоугодие. 5 Lizard также считается божеством танца и сексуального желания. 

Предлагаемые сочетания с едой: севиче, жареные креветки, зеленые тамале, крабовые котлеты и салат «Цезарь».','https://brewerydb-images.s3.amazonaws.com/beer/maOzc2/upload_y0efia-medium.png',4.3,NULL,1.03,1.006,0,19,1);

Мы взяли традиционный Schwarzbier, который задумывался как «пиво для сессий» с обычно умеренным содержанием алкоголя, и почти удвоили его крепость до 7,6% алкоголя по объему. Мы сварили нашу версию с прекрасным балансом кристаллического и жареного солода и приправили его благородным хмелем. Мы ферментировали 5 O’Clock Shadow при низких температурах с отборными немецкими лагерными дрожжами и выдерживали его в холодном состоянии более четырех месяцев, чтобы создать бархатистое, мягкое, согревающее зимнее время. 

Этот немецкий лагер имеет глубокий коричневый, почти черный цвет с рубиновыми бликами. Он имеет мягкие ноты темного шоколада с оттенками карамели и легким жареным послевкусием.','https://brewerydb-images.s3.amazonaws.com/beer/QsyRjm/upload_hftM4q-medium.png',7.6,22,1.044,1.01,0,4,1);

В ацтекской мифологии 5 Rabbit (Macuiltochtli) является одним из 5 богов излишеств (Ahuiateteo) и символизирует излишество гордости. Длинные уши кролика призывают нас учиться и слушать не только других, но и послания бытия и духа. 5 Rabbit также олицетворяет жизненную силу, счастье и плодородие. 

Предлагаемые сочетания с едой включают: Huachinango a la Veracruzana, целую жареную рыбу, чили rellenos, жареную курицу и пиццу.','https://brewerydb-images.s3.amazonaws.com/beer/jK6yuo/upload_HyfagC-medium.png',5.3,15,1.045,1.008,0,4,1);

В мифологии ацтеков 5 Vulture (Macuilcozcacuauhtli) является одним из 5 богов излишеств (Ahuiateteo) и символизирует избыток похоти и зависти. Падальщицкая натура стервятника представляет собой избавление от глубоко укоренившихся похоти и импульсов зависти в нашем существе. 5 Vulture также ассоциируется с мудростью и долголетием. 

Предлагаемые сочетания с едой включают: карне асада, блюда из колбасы чоризо, моле, кочинита пибиль и гамбургер','https://brewerydb-images.s3.amazonaws.com/beer/3AJboH/upload_4FNu5q-medium.png',6.4,NULL,1.03,1.006,0,8,1);

O.G: 1.056 ABV: 6.0% IBU: 30','https://brewerydb-images.s3.amazonaws.com/beer/yPfr6o/upload_ESmnbC-medium.png',6,5,1.03,1.006,0,25,1);

50 BACK — это лучший на вкус американский лагер премиум-качества, который вы найдете, но что делает его еще более исключительным, так это то, что он одновременно говорит «спасибо». 

Выбрав 50 BACK, вы позволили нам пожертвовать 50% наших доходов благотворительным организациям, которые стремятся напрямую поддерживать и обогащать жизни ветеранов нашей страны и их семей.','https://brewerydb-images.s3.amazonaws.com/beer/blMKT5/upload_e1h40j-medium.png',4.9,5,1.04,1.006,0,29,1);

Этот двойной IPA делает все дважды. Двойное количество хмеля, двойное содержание ABV = чертовски хорошее пиво. Названное в честь калифорнийских прибрежных секвой, одного из старейших и самых высоких видов в Америке, мы думаем, что это большое пиво воздает должное этим имперским гигантам.','https://brewerydb-images.s3.amazonaws.com/beer/EMiEgU/upload_whuzyK-medium.png',8.3,65,1.075,1.012,0,24,1);

ОТЛИЧНЫЙ ВКУС. ОТЛИЧНЫЙ АРОМАТ. ВЫ СТАВИТЕ.','https://brewerydb-images.s3.amazonaws.com/beer/RNhbEt/upload_dsaDFX-medium.png',7,50,1.06,1.012,0,2,1);
Летний сессионный эль Renegade — это рецепт пилснера, ферментированного с использованием элевых дрожжей для получения легкого тела, но фруктового послевкусия.','https://brewerydb-images.s3.amazonaws.com/beer/oW8fX2/upload_ZF7suw-medium.png',5,NULL,NULL,NULL,0,24,1);

Происхождение названия: Этот американский светлый эль привлекает к себе внимание во всем мире? Так же, как река Кайахога, когда она внезапно загорелась в 1969 году и побудила принять Закон о чистой воде 1972 года.','https://brewerydb-images.s3.amazonaws.com/beer/xB8Ual/upload_xME4c8-medium.png',6,30,1.044,1.008,0,25,1);

Доступно: разливное, упаковка по 6 и 12 бутылок, кеги по 5 и 15,5 галлонов','https://brewerydb-images.s3.amazonaws.com/beer/KAiRTh/upload_8g1Vay-medium.png',5.3,30,1.06,1.014,0,5,1);

Burnt Rock — классический портер в британском стиле, сваренный с добавлением органических стручков ванили. Несмотря на то, что предполагает тяжелый аромат, у Burnt Rock хорошее, чистое послевкусие и питкость. 
История и стиль 

Burnt Rock был впервые сварен в 2012 году как часть нашей серии Pro-Am пива, разработанного пивоварами-любителями и сваренного в Beau’s — ура его создателю Патрику Буавеню! Выпуск Burnt Rock Vanilla Porter 2013 года завоевал золото на Mondial de la Bière в Монреале. 

Дегустационные заметки 

Благодаря богатым, роскошным ароматам ванили, напоминающим тонкий трубочный табак, отборные темные солода придают сбалансированные шоколадные и кофейные нюансы этому пиву средней полноты. Хмель добавляет приятное сухое послевкусие.','https://brewerydb-images.s3.amazonaws.com/beer/MY5aX7/upload_00S4KU-medium.png',5.6,20,1.04,1.006,0,3,1);

Для Burton Baton мы сначала варим две «нити» или партии пива: английский старый эль и имперский IPA. После ферментации отдельных сортов пива в наших нержавеющих емкостях, оба пива переливаются и смешиваются вместе в одном из наших больших дубовых резервуаров. Burton Baton выдерживается на дровах около месяца. 

Наслаждаясь Burton Baton, вы обнаружите потрясающую смесь цитрусовых нот северо-западного хмеля, смешивающихся с древесными, ванильными нотами дуба. Древесина также имеет тенденцию смягчать 10% пива — так что будьте осторожны! 

Burton Baton варится и выпускается три раза в год.','https://brewerydb-images.s3.amazonaws.com/beer/em9Lxc/upload_qATac6-medium.png',10,65,1.075,1.012,0,7,1);

