INSERT INTO "beers" VALUES (19,'''Lil Griz','American-Style Brown Ale','Намеренно прокладывая свой собственный путь, мы сварили этот выдержанный в бочках коричневый эль, веря, что есть место для новой категории крафтового пива: «Пиво, выдержанное в бочках с бурбоном». При содержании 6,8% ABV (Alc. 6,8% by Vol.), насыщенные шоколадные ноты и сливочность овсяных хлопьев легко оправдывают то, что это пиво принесло домой медаль FoBAB в этом году. Надеемся, вам понравится «Lil Griz».','https://brewerydb-images.s3.amazonaws.com/beer/ZQzj1u/upload_0hoJ8r-medium.png',6.8,25,1.04,1.01,0,23,1);
INSERT INTO "beers" VALUES (20,'''Merica','French & Belgian-Style Saison','Merica — это односолодовый фермерский эль с одним хмелем. Он варится с использованием солодового пилснера и хмеля Nelson Sauvin в количестве 3 фунтов на баррель. Пиво выдерживается с использованием 2 штаммов бреттов и винных дрожжей. Хмель Nelson придает пиву вкус белого вина, а Brettanomyces добавляет сочность.','https://brewerydb-images.s3.amazonaws.com/beer/sBTXRJ/upload_OFYowQ-medium.png',7.5,20,1.055,1.004,0,14,1);
