INSERT INTO "beers" VALUES (217,'Cacao','Brown Porter','','https://brewerydb-images.s3.amazonaws.com/beer/gAU7hg/upload_D2e7oP-medium.png',6.4,20,1.04,1.006,0,30,1);
INSERT INTO "beers" VALUES (218,'Cacao','American-Style Imperial Stout','Какао, древняя тайна, возникшая в Латинской Америке и почитаемая как пища богов и королей в древние времена, по-прежнему обладает силой переносить нас в высшие места. 
INSERT INTO "beers" VALUES (219,'Cacao Bender','Chocolate / Cocoa-Flavored Beer','Бендер, приготовленный из какао-бобов, стручков ванили и кофе.','https://brewerydb-images.s3.amazonaws.com/beer/XSsP1y/upload_NyHyMP-medium.png',5.1,15,1.03,1.006,0,22,1);
