INSERT INTO "beers" VALUES (481,'Carrack','Imperial Red Ale','Сварено с большим количеством кристаллического и ржаного солода и капелькой коричневого сахара, чтобы подчеркнуть слои солодовой сложности. Здоровая доза хмеля с последующим сухим охмелением с Summit и Cascade','https://brewerydb-images.s3.amazonaws.com/beer/qEWknE/upload_8TYCbZ-medium.png',10.2,55,1.08,1.02,0,28,1);
