INSERT INTO "beers" VALUES (483,'Carrie Ladd','Brown Porter','','https://brewerydb-images.s3.amazonaws.com/beer/acxlJC/upload_mUMn1I-medium.png',6.6,20,1.04,1.006,0,2,1);
INSERT INTO "beers" VALUES (484,'CARROLLWOOD MIRROR IMAGE','American-Style Pale Ale','Американский светлый эль - 5,9% Этот светлый эль в северо-восточном стиле отличается обильным добавлением хмеля Medusa и Callista.','https://brewerydb-images.s3.amazonaws.com/beer/Euk2Oj/upload_Sc5U13-medium.png',5.9,30,1.044,1.008,0,4,1);
INSERT INTO "beers" VALUES (485,'CARROLLWOOD ONE FOR ALL','American-Style India Pale Ale','Этот домашний IPA производится с использованием хмеля Galazy и Azacca.','https://brewerydb-images.s3.amazonaws.com/beer/rESqYA/upload_x6SNcy-medium.png',6.8,50,1.06,1.012,0,8,1);
INSERT INTO "beers" VALUES (486,'Carrot Cake','Experimental Beer (Lager or Ale)','Carrot Cake — это экспериментальный крепкий американский коричневый эль, разработанный по вкусу как традиционный морковный пирог. Богатая солодовая основа обеспечивает «тесто для пирога», которое поддерживает грецкие орехи, пекан, имбирь, душистый перец, корицу, кленовый сироп и морковное пюре для дополнительной глубины, сложности и вкуса. Хмель Simcoe и апельсиновая цедра добавляют легкий цитрусовый элемент в сочетании с ванилью и зефирным пухом, чтобы придать компоненту глазури из сливочного сыра.','https://brewerydb-images.s3.amazonaws.com/beer/LsXbEj/upload_HvRpHv-medium.png',8,NULL,NULL,NULL,0,14,1);
INSERT INTO "beers" VALUES (487,'Carrot Cake Cream Ale','American-Style Cream Ale or Lager','Налейте кусочек сладкого и сливочного морковного пирога. Смешанный с настоящей морковью, ананасом и лактозой с широким выбором специальных солодов для насыщенного вкуса пирога и солодовой основы.','https://brewerydb-images.s3.amazonaws.com/beer/IlWdda/upload_K6SsWX-medium.png',5.8,10,1.044,1.004,0,11,1);
