INSERT INTO "beers" VALUES (512,'Cascadian Dark Ale (Bourbon Barrel Aged)','Dark American-Belgo-Style Ale','','https://brewerydb-images.s3.amazonaws.com/beer/miUDfe/upload_xdWy9P-medium.png',0,NULL,NULL,NULL,0,12,1);
INSERT INTO "beers" VALUES (513,'Cascadian Pale Ale','American-Style Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/cH4LAE/upload_TCmHEA-medium.png',12,30,1.044,1.008,0,5,1);
INSERT INTO "beers" VALUES (514,'Cascadian Schwarzbier','German-Style Schwarzbier','Этот черный лагер сочетает в себе сильный характер жареного солода с обильными добавками ароматного хмеля. Magnum, Cascade, Columbus, Centennial и Cluster в котле, хмельной и сухой добавки придают хвойный, смолистый, легкий цитрусовый вкус и аромат. Горькое послевкусие контрастирует с полным солодовым телом.','https://brewerydb-images.s3.amazonaws.com/beer/8hKtlR/upload_1JYXyJ-medium.png',6.8,22,1.044,1.01,0,9,1);
INSERT INTO "beers" VALUES (515,'Cascara Cherry Sour','American-Style Sour Ale','Что такое каскара? Каскара — это высушенная кожица кофейной ягоды после удаления семян (зерен). С помощью наших хороших друзей из Fuego Coffee мы выбрали особую смесь каскары из Никарагуа, а затем добавили в это пиво свежезаваренный чай каскара перед розливом в кеги. Наслаждайтесь нотками шиповника, гибискуса, вишни, красной смородины, манго и даже табака, которые привносит каскара. Каскара идеально сбалансирована на слегка терпкой солодовой основе с настоящей кислой вишней. Хотя каскара не имеет вкуса кофе, в ее чае есть небольшое количество кофеина. Этот кислый напиток сварен с любовью вместе с солодами Pale и CaraAroma; хмелем Crystal; Lactobacillus.','https://brewerydb-images.s3.amazonaws.com/beer/bxtSP4/upload_LkSl4L-medium.png',5.5,NULL,NULL,NULL,0,4,1);
INSERT INTO "beers" VALUES (516,'Cascara – Saison with Dried Coffee Fruit','French & Belgian-Style Saison','Второе сотрудничество с нашими друзьями из WoodBurl Coffee Roasters, этот бельгийский сэзон, сваренный с каскарой (сушеные кофейные плоды), имеет мутный, золотистый цвет со светло-оранжевыми бликами. Добавление хмеля Jarrylo во время кипячения придает некоторые цитрусово-апельсиновые нотки и немного пряности. Солодовый характер легкий и свежий. Послевкусие очень сухое. 
INSERT INTO "beers" VALUES (517,'Cascazilla','American-Style Amber/Red Ale','Название CascaZilla (Cas-kuh-zil-uh) — это игра слов, основанная как на названии ущелья в Итаке под названием Cascadilla, так и на чудовищном количестве хмеля Cascade, которое мы используем для приготовления пива. Преобладающий вкус и аромат этого пива исходит от свежего американского хмеля. CascaZilla получает свой характерный красный цвет от здоровой доли карамельного солода, который также придает пиву крепкое тело и сладость. Это хмельной красный эль!','https://brewerydb-images.s3.amazonaws.com/beer/pBfN8o/upload_zHkIMf-medium.png',7,30,1.048,1.012,0,24,1);
INSERT INTO "beers" VALUES (518,'Casco Bay Brown Ale','American-Style Brown Ale','','https://brewerydb-images.s3.amazonaws.com/beer/uOedGS/upload_GxPjw2-medium.png',0,25,1.04,1.01,0,4,1);
INSERT INTO "beers" VALUES (519,'Casco Bay IPA','American-Style India Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/pagwfF/upload_1AUZuN-medium.png',5.75,50,1.06,1.012,0,13,1);
INSERT INTO "beers" VALUES (520,'Cash','American-Style Imperial Stout','Imperial Coffee Stout Совместная работа с Mostra Coffee. Ноты темного шоколада, эспрессо и насыщенного солода','https://brewerydb-images.s3.amazonaws.com/beer/Y2ZoXO/upload_wTIbZH-medium.png',9.4,50,1.08,1.02,0,20,1);
INSERT INTO "beers" VALUES (521,'Cash Stout','Oatmeal Stout','','https://brewerydb-images.s3.amazonaws.com/beer/z16jTF/upload_btxtAm-medium.png',6,20,1.038,1.008,0,1,1);
INSERT INTO "beers" VALUES (522,'Cashmere Hammer','Oatmeal Stout','Нам очень нравится варить пиво с ржаным хмелем. У него сухая, пряная характеристика, которая отлично сочетается с другими вкусами. Во многих стилях пива можно использовать немного ржи, чтобы сбалансировать в остальном однонаправленный вкус. Однако для этого пива мы начали с ржи и добавили ее, гарантируя, что она будет играть первую скрипку. Остальные ингредиенты — базовый, темный и карамельный солод, а также чешский жатецкий хмель — были выбраны за их способность подчеркивать рожь и играть ее смелость. 
INSERT INTO "beers" VALUES (523,'Casilda of Toledo','Vienna-Style Lager','Святая Касильда Толедская, Испания, однажды заставила хлеб, спрятанный под юбкой, чудесным образом превратиться в розы. Да, это произошло. Этот венский лагер может похвастаться прекрасными меланоидиновыми вкусами поджаренного солода. Он завершается легкой сладостью и легким, цветочным благородным хмелевым поцелуем — идеальное сочетание хлеба и цветов для нашей сладкой Касильды.','https://brewerydb-images.s3.amazonaws.com/beer/LM7YFp/upload_MTJZyf-medium.png',5.3,22,1.046,1.012,0,28,1);
INSERT INTO "beers" VALUES (524,'Casino RoyALE','American-Style Pale Ale','Этот Golden American Pale Ale чистый и мягкий, а хмель 007 демонстрирует тонкий и цитрусовый аромат и вкус. 
INSERT INTO "beers" VALUES (525,'Cask Ale','Kellerbier (Cellar beer) or Zwickelbier - Lager','','https://brewerydb-images.s3.amazonaws.com/beer/HtU5mR/upload_PysWw0-medium.png',0,NULL,NULL,NULL,0,22,1);
INSERT INTO "beers" VALUES (526,'Cask-Aid','Specialty Beer','','https://brewerydb-images.s3.amazonaws.com/beer/80tC1T/upload_ADO8LM-medium.png',5.5,NULL,1.03,1.006,0,18,1);
INSERT INTO "beers" VALUES (527,'Casket IPA','International-Style Pale Ale','Для создания этого освежающего черного пива с хмельным ароматом использовался свежий влажный хмель сорта Cascade от местного производителя.','https://brewerydb-images.s3.amazonaws.com/beer/Ebi9Rr/upload_MUilqI-medium.png',6.3,30,1.044,1.008,0,24,1);
INSERT INTO "beers" VALUES (528,'Casper The Friendly Gose','Leipzig-Style Gose','Gose (произносится как го-су) — немецкий стиль пива, который до недавнего времени был почти вымершим. Освежающее наполовину пшеничное, наполовину пилснерское солодовое пиво с нотками кориандра, лимона и морской соли, которые придают ему озорной оттенок. Отличное знакомство со стилем Gose!','https://brewerydb-images.s3.amazonaws.com/beer/04k0TG/upload_itd1oi-medium.png',5,10,1.036,1.008,0,4,1);
INSERT INTO "beers" VALUES (529,'Cass Fresh','American-Style Premium Lager','','https://brewerydb-images.s3.amazonaws.com/beer/j04x5I/upload_iZ3PWr-medium.png',0,6,1.044,1.01,0,7,1);
INSERT INTO "beers" VALUES (530,'Cass River Blonde Ale','Golden or Blonde Ale','Этот классический эль производится только из солода и ферментируется при более низкой температуре, чтобы придать ему чистый финиш. Легко пьется, низкоалкогольный. Это великолепное кроссоверное пиво для тех, кто любит американский эль.','https://brewerydb-images.s3.amazonaws.com/beer/RfAIpe/upload_RsCiuG-medium.png',4.75,15,1.045,1.008,0,30,1);
INSERT INTO "beers" VALUES (531,'Casserly Pale Ale','Belgian-Style Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/G2UQKh/upload_JKqL2n-medium.png',5.2,20,1.044,1.008,0,5,1);
INSERT INTO "beers" VALUES (532,'Cassia Saison','French & Belgian-Style Saison','Этот прекрасный напиток был сварен для Beervana 2019. Вдохновленный вкусами Таиланда и разработанный для сочетания с Harraways Harr-os Thai Sweet Chilli Pumpkin Oat Bites. С премиальным светлым солодом пилснер и тонким оттенком ржи. В водоворот добавлены листья лайма, поджаренный кориандр, свежий тайский базилик и экспериментальный хмель #07270. Во время вторичного брожения были добавлены поджаренные кокосовые волокна, свежевыжатый сок лайма и хмель Sorachi Ace. С этим пивом много чего происходит, но все на своих местах.','https://brewerydb-images.s3.amazonaws.com/beer/q7zQP7/upload_Kiokyo-medium.png',7,20,1.055,1.004,0,21,1);
INSERT INTO "beers" VALUES (533,'Cassian Sunset','American-Style Imperial Stout','Выдержанный в бочках из-под бурбона имперский стаут ​​с местным кофе Emy J''s, цельными бобами ванили и корицей. Вкусовой профиль этого замечательного пива может похвастаться пышным вкусом, с правильным количеством сливочно-пряного характера, сочетающегося с богатым солодовым и выдержанным в бочках профилем.','https://brewerydb-images.s3.amazonaws.com/beer/LA9SnJ/upload_kcxvLn-medium.png',11.7,50,1.08,1.02,0,25,1);
INSERT INTO "beers" VALUES (534,'Cassidy','American-Style Sour Ale','Кислый блонд-эль, выдержанный в бочке в течение 6 месяцев с использованием смешанной культуры. Терпкий с ароматом дуба и ванили, цитрусовых, напоминающий абрикос и мандарины, который уступает место мускусному, белому виноградному оттенку и сухому, чистому послевкусию.','https://brewerydb-images.s3.amazonaws.com/beer/XaD1Qb/upload_rhUDMm-medium.png',5.3,NULL,NULL,NULL,0,15,1);
INSERT INTO "beers" VALUES (535,'Cassiopeia','American-Style Imperial Porter','CASSIOPEIA — имперский портер, сваренный к началу зимы. Черный как ночное небо, наш имперский портер варится из смеси отборных темных солодов, отобранных за их сложные карамельные/шоколадные/фруктовые качества. Холодное брожение с чистыми шотландскими элевыми дрожжами, которые подчеркивают солодовую насыщенность и землистую, мшистую ароматику — представьте себе Cassiopeia как балтийский портер, сброженный как Wee Heavy. Этот бархатистый, согревающий эль на удивление хорошо выброжен, легок на вкус и легко усваивается.','https://brewerydb-images.s3.amazonaws.com/beer/bzeCwh/upload_NNjg7t-medium.png',9.3,35,1.08,1.02,0,18,1);
INSERT INTO "beers" VALUES (536,'Cassis','Fruit Beer','Это прекрасное пшеничное пиво производится проверенным временем способом, путем добавления фруктов в пиво во время выдержки, чтобы дать возможность бельгийским дрожжам провести вторичную ферментацию. В результате получается освежающе сухое пиво с легким вкусом «черной смородины» и слегка мутно-розовым цветом. Лето в бокале!','https://brewerydb-images.s3.amazonaws.com/beer/bR0uM4/upload_ELNWEO-medium.png',4.8,5,1.03,1.006,0,7,1);
INSERT INTO "beers" VALUES (537,'Cassis','Belgian-Style Fruit Lambic','','https://brewerydb-images.s3.amazonaws.com/beer/Lz3dwE/upload_nTgIA4-medium.png',4,15,1.04,1.008,0,6,1);
INSERT INTO "beers" VALUES (538,'Cassis','Belgian-Style Fruit Lambic','Lambic with Champagne Currants 
INSERT INTO "beers" VALUES (539,'Cassis','Belgian-Style Flanders Oud Bruin or Oud Red Ales','Кислый красный эль во фландрийском стиле, выдержанный 12 месяцев в дубовых бочках с терпкой вишней и черной смородиной.','https://brewerydb-images.s3.amazonaws.com/beer/jhraeK/upload_DWYqeW-medium.png',6.2,15,1.044,1.008,0,29,1);
INSERT INTO "beers" VALUES (540,'Cassissona','American-Style Amber/Red Ale','Буйное, шипучее, сладкое Cassissona, пиво с ударом, имеет богатый, сложный фруктовый аромат - в первую очередь черной смородины - помимо других инновационных и интенсивных вкусов и запахов. Оно было разработано для нескольких очень особых ситуаций и лучше всего подходит для подачи с фруктовыми десертами или - еще одна допустимая альтернатива - пикантными сырами. Определенный "винный" вкус делает его идеальным для печенья "Cantucci" из Сиены, например. 
INSERT INTO "beers" VALUES (541,'Cast Iron Oatmeal Brown','American-Style Brown Ale','Это монстр среди коричневых элей! Мы используем отвратительное количество шоколадного солода и жареного ячменя, чтобы придать этому пиву железную основу. Наш овсяный коричневый цвет льется темно-красным деревом с ароматами темного шоколада и кофе.','https://brewerydb-images.s3.amazonaws.com/beer/VOmg95/upload_92mYHX-medium.png',5.5,25,1.04,1.01,0,8,1);
INSERT INTO "beers" VALUES (542,'Cast No Shadow on Liberty','American-Style India Pale Ale','Назван в честь городского указа 1922 года, согласно которому ни одно здание не должно строиться так, чтобы оно могло отбрасывать тень на Свободу, статую, украшающую вершину Монумента солдатам и морякам в центре Индианаполиса. Этот IPA сочетает в себе американские и тихоокеанские хмели с нашим домашним штаммом Brettanomyces, создавая слегка фанковый, цитрусовый IPA.','https://brewerydb-images.s3.amazonaws.com/beer/BAuhEr/upload_ZgyWsf-medium.png',5.6,50,1.06,1.012,0,6,1);
INSERT INTO "beers" VALUES (543,'Cast Out','American-Style India Pale Ale','В это время года нет более желанного хмеля, чем Galaxy из Австралии, которая изначально была заселена изгнанными английскими каторжниками. Outcast IPA щедро использует этот хмель от стадии охмеления до стадии сухого охмеления. Тем не менее, результатом является сбалансированный и бронзовый эль со средней хмелевой горечью, дополненной умеренной солодовостью, демонстрирующий ароматы и вкусы, напоминающие маракуйю. Выпущенный вслед за охмеленным Northwest Insider IPA, Outcast является тайным рукопожатием между полушариями.','https://brewerydb-images.s3.amazonaws.com/beer/efvPr0/upload_vhDMUs-medium.png',7.6,50,1.06,1.012,0,10,1);
INSERT INTO "beers" VALUES (544,'CastagnAle','Specialty Beer','Мягкое, определенное каштановое пиво. Каштаны высушиваются дымом на каштановой древесине и используются в пиве в количестве 50%. Пиво имеет бледно-янтарный цвет с очень тонкой пенной шапкой цвета слоновой кости. Аромат слегка копченый с нотками каштанового меда. Ощущение во рту мягкое и соответствует его начальным нотам.','https://brewerydb-images.s3.amazonaws.com/beer/pCpuUf/upload_lQJzFt-medium.png',4.1,NULL,1.03,1.006,0,26,1);
INSERT INTO "beers" VALUES (545,'Castaway','German-Style Kölsch / Köln-Style Kölsch','','https://brewerydb-images.s3.amazonaws.com/beer/eo1vbE/upload_d6Z50y-medium.png',0,18,1.042,1.006,0,1,1);
INSERT INTO "beers" VALUES (546,'Castaway Blonde Ale','Golden or Blonde Ale','','https://brewerydb-images.s3.amazonaws.com/beer/iSqRNZ/upload_5pST2D-medium.png',5.5,15,1.045,1.008,0,24,1);
INSERT INTO "beers" VALUES (547,'Castaway Chocolate Orange IPA','American-Style India Pale Ale','Этот хрустящий и цитрусовый индийский светлый эль приобретает свой нетипичный темно-медно-коричневый цвет благодаря солодовой шоколадной пшенице. Сильно охмеленный, горьковатый шоколадный апельсиновый IPA смешивается с темным шоколадным вкусом какао-бобов, добавленных во время ферментации. Эти тонкие шоколадные ноты лежат за цветочным букетом, усиленным добавлением горькой апельсиновой цедры и сухим охмелением с помощью Citra.','https://brewerydb-images.s3.amazonaws.com/beer/7B6a7a/upload_ROmo6H-medium.png',7.5,50,1.06,1.012,0,12,1);
INSERT INTO "beers" VALUES (548,'Castaway IPA','American-Style India Pale Ale','Насыщенный IPA в американском стиле бронзового цвета, Castaway IPA «сухого охмеления» (выдержанный с большим количеством цельных цветков хмеля после ферментации) для получения ярко выраженного хмелевого вкуса и аромата. Горечь уравновешивается солодовым телом пива.','https://brewerydb-images.s3.amazonaws.com/beer/p4yvy6/upload_f9ixIE-medium.png',6,50,1.06,1.012,0,10,1);
INSERT INTO "beers" VALUES (549,'Castelain Blond Biere de Garde','French-Style Bière de Garde','','https://brewerydb-images.s3.amazonaws.com/beer/veC8f9/upload_YfhuYi-medium.png',6.4,20,1.06,1.012,0,5,1);
INSERT INTO "beers" VALUES (550,'Castelain Ch’Ti Blonde','French-Style Bière de Garde','Бьер де Гард из Франции; 
INSERT INTO "beers" VALUES (551,'Castillo Classic IPA','English-Style India Pale Ale','Наш дважды удостоенный золотой медали английский IPA на GABF 2018 и California State Fair 2019! - Классический IPA, демонстрирующий нюансы и баланс, которые вывели IPA на новый уровень. Изготовлен с использованием идеального сочетания американского и английского светлого солода для великолепного медного цвета и прочной солодовой основы, тонко сбалансированный с цветочными, цитрусовыми и травяными хмелевыми ароматами и вкусом из различных хмелей "C". "Король замка"','https://brewerydb-images.s3.amazonaws.com/beer/78OG4X/upload_5cxS2U-medium.png',6.8,35,1.05,1.012,0,28,1);
INSERT INTO "beers" VALUES (552,'Castillo Coconut Porter','Brown Porter','Этот единственный в своем роде портер — приятная смесь темного шоколада и черного солода. Он сварен с сырым органическим кокосом и доварен хмелем Northern Brewer.','https://brewerydb-images.s3.amazonaws.com/beer/mkb2qJ/upload_gha5Gb-medium.png',6.8,20,1.04,1.006,0,15,1);
INSERT INTO "beers" VALUES (553,'Castle Bravo','American-Style India Pale Ale','IPA, предназначенный для тестирования, с расчетным выходом, соответствующим параметрам стандартного индийского светлого эля. Тесты показали выход, примерно в три раза превышающий предложенный. Этот IPA сочетает в себе три разных вида хмеля, которые на языке образуют самый взрывной IPA из всех, что мы пробовали. Несмотря на все наши усилия, он был выпущен на волю публики. Наслаждайтесь последствиями!','https://brewerydb-images.s3.amazonaws.com/beer/w3x29x/upload_B7RVRY-medium.png',11,50,1.06,1.012,0,15,1);
INSERT INTO "beers" VALUES (554,'Castle Cream Ale','American-Style Cream Ale or Lager','Сидя на кухне в первые дни нашей работы, мы поняли, что нам нужно пиво, которым можно насладиться. Наш первый стажер на самом деле придумал идею сварить кремовый эль, поэтому мы начали разрабатывать рецепт. Это наша версия кремового эля. Глубокого золотистого цвета, с мягким солодовым ароматом, слегка сладковатой кремовой текстурой со сбалансированной горечью, а финиш гладкий и чистый','https://brewerydb-images.s3.amazonaws.com/beer/sJn11y/upload_JECx7t-medium.png',5.5,10,1.044,1.004,0,1,1);
INSERT INTO "beers" VALUES (555,'Castle Rock Red Riding Hood','Ordinary Bitter','Представленный на фестивале пива и сидра Robin Hood 2014, Red Riding Hood — новейшее пиво в основной линейке пива Castle Rock. Красновато-коричневый цвет, Red Riding Hood заполняет пробел между самыми традиционными лучшими биттерами и фруктовыми красными элями из США и других стран. Red Riding Hood варится с использованием сортов El Dorado (США) и Kohatu (Новая Зеландия), что придает ему вкус мягких косточковых фруктов. 
INSERT INTO "beers" VALUES (556,'Castlebrite Apricot Ale','Fruit Beer','Castlebrite — первое фруктовое пиво от Dragonmead. В этом Apricot Ale используется абрикосовое пюре, а также солод Pale Wheat, чтобы создать это чудесно освежающее пиво. Это пиво начинается с тонкой сладости и легкого абрикосового привкуса, а заканчивается очищающей вкус терпкостью, которая заставит вас хотеть большего.','https://brewerydb-images.s3.amazonaws.com/beer/jFJ7Ni/upload_ETsxxw-medium.png',4.7,5,1.03,1.006,0,29,1);
INSERT INTO "beers" VALUES (557,'Castover','Belgian-Style Pale Ale','Castover — золотистый эль средней плотности, обладающий сложным вкусом с нотками косточковых фруктов и маслянистым послевкусием.','https://brewerydb-images.s3.amazonaws.com/beer/okvcRy/upload_QhwSKs-medium.png',8.5,20,1.044,1.008,0,21,1);
INSERT INTO "beers" VALUES (558,'Cat Dragon','Specialty Beer','«Cat Dragon» — самое крепкое имбирное пиво на планете. Почувствуйте запах имбиря и алкоголя, а затем вкус игристой сладости. Почувствуйте, как жжение катится по вашему горлу. Крепкое, без глютена и чертовски вкусное! Также используется для версии московского мула, долбаного «Огнедышащего кота-дракона»','https://brewerydb-images.s3.amazonaws.com/beer/dLJE0w/upload_DdNR2V-medium.png',12.5,NULL,1.03,1.006,0,30,1);
INSERT INTO "beers" VALUES (559,'Cat Fight','American-Style India Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/Lrqqda/upload_bMDz7b-medium.png',6.7,50,1.06,1.012,0,5,1);
INSERT INTO "beers" VALUES (560,'Cat In the Wall','American-Style India Pale Ale','Теперь ты говоришь на моем языке. IPA в стиле Новой Англии.','https://brewerydb-images.s3.amazonaws.com/beer/i8lqAp/upload_aPmMas-medium.png',7,50,1.06,1.012,0,19,1);
INSERT INTO "beers" VALUES (561,'Cat Lady','American-Style Brown Ale','','https://brewerydb-images.s3.amazonaws.com/beer/Qvoaso/upload_T2LoXO-medium.png',5.8,25,1.04,1.01,0,5,1);
INSERT INTO "beers" VALUES (562,'Cat Lady IPA','American-Style India Pale Ale','Этот острый рецепт IPA был подобен фениксу, восставшему из пепла хмелевой политики. Столкнувшись с серьезной нехваткой Citra в начале осени (2013), нам пришлось на время приостановить производство Roman Candle. В его отсутствие и чтобы залечить наши разбитые сердца, мы разработали рецепт, который был полностью посвящен использованию острого цитрусового удара двух хмелей, которые мы называем здесь фаворитами: Summit и Simcoe. Тело солода чрезвычайно легкое, что позволяет сосредоточиться на хмеле, как в аромате, так и в горечи. 
INSERT INTO "beers" VALUES (563,'Cat Spit Stout Ale','American-Style Stout','','https://brewerydb-images.s3.amazonaws.com/beer/6h4BDc/upload_LK0A0Y-medium.png',6,35,1.05,1.01,0,3,1);
INSERT INTO "beers" VALUES (564,'Cat''s In the Cradle','American-Style India Pale Ale','Двойной охмеленный IPA с хмелем Simcoe','https://brewerydb-images.s3.amazonaws.com/beer/4lR2hz/upload_nTZZx5-medium.png',7.5,50,1.06,1.012,0,24,1);
INSERT INTO "beers" VALUES (565,'Cat''s Pajamas','Imperial or Double India Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/If8J3l/upload_cKSz4i-medium.png',9,65,1.075,1.012,0,17,1);
INSERT INTO "beers" VALUES (566,'Catahoula Common','American-Style Lager','Catahoula Common — освежающий американский лагер, названный в честь государственной собаки Луизианы. Легкое тело и четкий финиш Catahoula делают его приятным напитком в нашем влажном климате и особенно утоляют жажду в жаркие летние дни. Catahoula Common использует 2-Row, Crystal 15, солодовый ячмень Carapils с добавлением Cascade и ферментируется с помощью дрожжей California Common.','https://brewerydb-images.s3.amazonaws.com/beer/xHhDdn/upload_wbDPpr-medium.png',5.5,5,1.04,1.006,0,7,1);
INSERT INTO "beers" VALUES (567,'Catalyst','Imperial or Double India Pale Ale','Этот IPA Третьего побережья варится с невероятным количеством хмеля и ровно такой солодовой сладостью, чтобы создать мягкое, восхитительное послевкусие. «Сваренный» с восемью различными сортами хмеля, Catalyst имеет сложную и уникальную смесь цитрусовых, хвойных и сырых нот. Достаточно приятный и интенсивный, чтобы дать встряску даже самой большой хмелевой голове.','https://brewerydb-images.s3.amazonaws.com/beer/aP5oWS/upload_GqrjgW-medium.png',9.6,65,1.075,1.012,0,30,1);
INSERT INTO "beers" VALUES (568,'Catch 23','American-Style Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/QmUyPV/upload_Hmptuw-medium.png',5.6,30,1.044,1.008,0,22,1);
INSERT INTO "beers" VALUES (569,'Catch of a Lifetime','French & Belgian-Style Saison','','https://brewerydb-images.s3.amazonaws.com/beer/JsIANW/upload_241qqI-medium.png',8,20,1.055,1.004,0,30,1);
INSERT INTO "beers" VALUES (570,'Catch Wave','American-Style India Pale Ale','Яркий, мутный и вкусный. Двойное сухое охмеление с помощью El Dorado, Hallertau Blanc, Mosaic и Citra, вкусы четкие и освежающие с выраженными нотами дыни, персика, личи и зефирного пуха.','https://brewerydb-images.s3.amazonaws.com/beer/o0YTvs/upload_ZfPshS-medium.png',7.5,50,1.06,1.012,0,30,1);
INSERT INTO "beers" VALUES (571,'Catcher in the Rye','Rye Ale or Lager with or without Yeast','Немецкий солод Pilsner, американский Rye и британский Crystal Rye создают прекрасное оранжево-красное пиво с хрустящей пряностью ржаного солода, дополненное сосной, цитрусовыми и свежим имбирем из американского хмеля Chinook. Освежающее, утоляющее жажду, но с полным вкусом, с бисквитным солодовым привкусом, подкрепляющим вкус и аромат хмеля.','https://brewerydb-images.s3.amazonaws.com/beer/WGQZzy/upload_8kg4Rt-medium.png',5,NULL,NULL,NULL,0,14,1);
INSERT INTO "beers" VALUES (572,'Catcher in the Rye Ale','Rye Ale or Lager with or without Yeast','Доступно круглый год ржаной светлый эль','https://brewerydb-images.s3.amazonaws.com/beer/NFJBcg/upload_4yQv6M-medium.png',5.5,NULL,NULL,NULL,0,14,1);
INSERT INTO "beers" VALUES (573,'Category 5 IPA','Imperial or Double India Pale Ale','Самый крепкий IPA в серии Due South Category, Cat 5 — это пиво с большим количеством хмеля и большим количеством вкуса. При содержании 8,5% ABV и 96 IBU первые полосы солода уступят место всплеску хмеля во вкусе. Созданное для настоящих любителей хмеля, это пиво не для слабонервных.','https://brewerydb-images.s3.amazonaws.com/beer/kDRmFU/upload_ucUNvQ-medium.png',8.5,65,1.075,1.012,0,17,1);
INSERT INTO "beers" VALUES (574,'Caterpillar Pale Ale','German-Style Pilsener','','https://brewerydb-images.s3.amazonaws.com/beer/bYhUPQ/upload_7zsL3a-medium.png',5.8,25,1.044,1.006,0,15,1);
INSERT INTO "beers" VALUES (575,'CatFight – Saison','French & Belgian-Style Saison','Образ CatFight вдохновлен уникальным добавлением хмеля Aramis на протяжении всего процесса пивоварения. Сорт Aramis (названный в честь одного из 3 мушкетеров) демонстрирует тонкие ароматы, унаследованные от французской линии Strisselspalt. Они имеют сладкие, пряные ноты с оттенками цитрусовых и трав. Говорят, что Арамис Мушкетер любил женщин и изображался как постоянно амбициозный и ненасытный. Точно так же, как и вы, попробовав этот Saison, с его легким и свежим солодовым вкусом с нотками лимона/цитруса и сухим послевкусием. Вы будете жаждать большего с каждым глотком! 
INSERT INTO "beers" VALUES (576,'Cathedral Square Forbidden Fruit Apple Ale','Fruit Beer','','https://brewerydb-images.s3.amazonaws.com/beer/uyuyH2/upload_2KT7kq-medium.png',5,5,1.03,1.006,0,15,1);
INSERT INTO "beers" VALUES (577,'Catherine Wheel','American-Style India Pale Ale','Это пиво, которое призывает к изысканности двух стилей, которые мы любим: IPA и бельгийские эли. Освежающее и ароматное, Catherine Wheel — это нетрадиционный бельгийско-американский меланж.','https://brewerydb-images.s3.amazonaws.com/beer/0r8pPZ/upload_hwUa8W-medium.png',7.2,50,1.06,1.012,0,7,1);
INSERT INTO "beers" VALUES (578,'Catherine''s Wood','American-Style Imperial Stout','Catherine''s Wood — выдержанный в бочках русский императорский стаут','https://brewerydb-images.s3.amazonaws.com/beer/Zigg9i/upload_UY9opK-medium.png',0,50,1.08,1.02,0,21,1);
INSERT INTO "beers" VALUES (579,'Cathy''s Cure','American-Style Cream Ale or Lager','Raspberry Cream Ale, разработанный и сваренный для одного из членов нашей семьи, борющегося с раком. Вкусное пиво для достойного дела.','https://brewerydb-images.s3.amazonaws.com/beer/ZwCfHj/upload_HGMP6A-medium.png',6,10,1.044,1.004,0,28,1);
INSERT INTO "beers" VALUES (580,'Catnip','American-Style India Pale Ale','Супербледный, односолодовый (Best Malz Pilsner), однохмелевой (Chinook) IPA, вдохновленный эффектом кошачьей мяты на нашего пивного кота, Фаггла. Если бы только пиво могло сделать нас такими счастливыми... Catnip — наш лучший шанс — светлое, обманчиво крепкое и хмельное пиво со вкусами грейпфрута и хмеля, а также освежающей терпкостью и «фанком» от наших бельгийских дрожжей Wit.','https://brewerydb-images.s3.amazonaws.com/beer/Xy6cyP/upload_2vMX27-medium.png',7.1,50,1.06,1.012,0,10,1);
INSERT INTO "beers" VALUES (581,'Catoctin Ale','Ordinary Bitter','Более янтарный и менее «хмелевой», чем его американский аналог, обилие кристаллического солода создает хорошо сбалансированное послевкусие для нашей вариации этого английского стиля.','https://brewerydb-images.s3.amazonaws.com/beer/wdjaPp/upload_sqEnF0-medium.png',5.5,20,1.033,1.006,0,10,1);
INSERT INTO "beers" VALUES (582,'Catoctin Clear Lager','German-Style Kölsch / Köln-Style Kölsch','','https://brewerydb-images.s3.amazonaws.com/beer/WvhkO5/upload_JCvi7K-medium.png',4.2,18,1.042,1.006,0,30,1);
INSERT INTO "beers" VALUES (583,'Cattail Pale Ale','American-Style Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/GYTFw7/upload_W1giXS-medium.png',0,30,1.044,1.008,0,9,1);
INSERT INTO "beers" VALUES (584,'Catty Shack','American-Style India Pale Ale','Отдавая дань уважения хмелю Simcoe; этот дробимый IPA имеет нотку кристаллического солода, конфетную сладость, чтобы сбалансировать хмелевой аромат и вкус. Некоторые могут почувствовать кошачий характер от Simcoe; однако, мы думаем, что это крутой хмель. Яркие вкусы цитрусовых, сосны и дыни; за которыми следует аромат тропических фруктов и дыни. Этот IPA свежий, чистый и очень вкусный. 6,0% ABV','https://brewerydb-images.s3.amazonaws.com/beer/S04lQ8/upload_rOdypf-medium.png',6,50,1.06,1.012,0,4,1);
INSERT INTO "beers" VALUES (585,'CattyWompus','American-Style India Pale Ale','Смесь традиций старого мира. Сваренный как IPA, он сочетает в себе ароматы хмеля соснового и цитрусового вкусов с оттенками ананаса и груши из секретного бельгийского штамма дрожжей.','https://brewerydb-images.s3.amazonaws.com/beer/az9Bgb/upload_Z3aO6E-medium.png',7.5,50,1.06,1.012,0,11,1);
INSERT INTO "beers" VALUES (586,'Caught In the Rain','American-Style Sour Ale','Кислый IPA с лактозой, ванилью и ананасом.','https://brewerydb-images.s3.amazonaws.com/beer/BmYXRj/upload_Ryfd8r-medium.png',6.7,NULL,NULL,NULL,0,1,1);
INSERT INTO "beers" VALUES (587,'Cauldren Contents','Juicy or Hazy India Pale Ale','ТУМНЫЙ, СОЧНЫЙ IPA С ОГРОМНЫМ ВИРПУЛОМ И ДВОЙНЫМИ ДОБАВКАМИ СУХОГО ОХМЕЛЕНИЯ ELLA, EL DORADO, EUREKA И CITRA. 
INSERT INTO "beers" VALUES (588,'Cauldron','American-Style Sour Ale','Природа пивоварения по своей сути подразумевает сочетание ингредиентов для создания этого конечного хорошо сбалансированного пива. Внутри этого сосуда находится часть Malefactor, Epic Flanders-Style Red Ale и одна часть Dantalion Dark Wild Ale. Эти сорта пива были смешаны и выдержаны на вишне сорта Монморанси из северного Мичигана. Слияние этих двух сортов пива создает сложный кислый эль с нотками специй, обугленного дуба и обильным характером вишни. Смешанный, чтобы насладиться алхимиком внутри каждого из нас.','https://brewerydb-images.s3.amazonaws.com/beer/z2Wo2l/upload_Num3Rs-medium.png',0,NULL,NULL,NULL,0,4,1);
INSERT INTO "beers" VALUES (589,'Cauldron','Classic Irish-Style Dry Stout','Сухой ирландский стаут ​​с диким оттенком. Сваренный с 6,66% кислого сусла и подаваемый только с азотом, этот стаут ​​мягкий, сливочный и поджаренный с нотками темного шоколада и кофе и лишь намеком на сложность от небольшой порции терпкого сусла.','https://brewerydb-images.s3.amazonaws.com/beer/9bahEJ/upload_9GYsyw-medium.png',4.1,30,1.038,1.008,0,23,1);
INSERT INTO "beers" VALUES (590,'Cavalier','French & Belgian-Style Saison','На нашей пивоварне мы уделяем много времени планированию, особенно когда дело касается прогнозирования ингредиентов и материалов, используемых для приготовления пива. Однако иногда у нас остаются некоторые «разнородные вещи», которые позволяют нам проверить нашу креативность. У нас были полезные французские дрожжи для сэзона от предыдущего варения, которые мы решили соединить с пюре из личи, которому также нужен был дом. Мне понравилось, куда направлялось пиво, но после добавления Brettanomyces Lambicus я понял, что у нас есть что-то особенное. Cavalier сложный, но на удивление цельный. Дань старому миру, но соотносимый с сегодняшним постоянно меняющимся пивным ландшафтом.','https://brewerydb-images.s3.amazonaws.com/beer/hlOakx/upload_bP0zZp-medium.png',8.8,20,1.055,1.004,0,19,1);
INSERT INTO "beers" VALUES (591,'Cavalry Scout','Foreign (Export)-Style Stout','более охмелено, чем Guinness Draught и Extra Stout, и обычно имеет более высокое содержание алкоголя (около 7,5% ABV), что придает ему более горький вкус','https://brewerydb-images.s3.amazonaws.com/beer/0b6COK/upload_YDlM1P-medium.png',5.7,30,1.052,1.008,0,25,1);
INSERT INTO "beers" VALUES (592,'Cavatica Stout','American-Style Stout','Огромный, смелый, черный как ночь, американский стаут-красавец; Cavatica не сухой ирландский сорт, один глоток, и вы наверняка заметите, что пьете. Название отдает дань уважения близости этого пивовара к паутинке. Будьте осторожны, это пиво оставит пятна на вашей одежде. Это домашний стаут ​​Форт-Джорджа, который приобрел репутацию крепкого и заразного среди местных жителей.','https://brewerydb-images.s3.amazonaws.com/beer/rS7pUT/upload_1yHhNQ-medium.png',8.8,35,1.05,1.01,0,22,1);
INSERT INTO "beers" VALUES (593,'Cave Ale','American-Style Amber/Red Ale','Пещеры, высеченные в холмах Потоси, когда-то использовались для выдержки и хранения пива. Названный в соответствии с этой традицией, наш мягкий английский стиль Amber Ale может похвастаться сложными вкусами жареного карамельного солода, идеально сбалансированного с хмелем Golding.','https://brewerydb-images.s3.amazonaws.com/beer/oeiy1y/upload_nAHSJI-medium.png',5.5,30,1.048,1.012,0,24,1);
INSERT INTO "beers" VALUES (594,'Cave City Lager','American-Style Dark Lager','Немецкий Dunkel вдохновил американский янтарный лагер с фирменным ароматом хмеля. Этот лагер сочетает в основном мюнхенский солод с добавлением мягко сбалансированных карамелизированных солодов, что придает ему насыщенный медный цвет, а также немного больше солодовой основы и общего характера, чем типичный янтарный лагер. Сложное сочетание благородных хмелей позволяет сочетать наш лагер с широким спектром блюд, а его чистое лагерное послевкусие создает пиво, которое действительно придется по вкусу в эти жаркие летние дни.','https://brewerydb-images.s3.amazonaws.com/beer/6oSeg5/upload_UAJqXv-medium.png',5.2,14,1.04,1.008,0,30,1);
INSERT INTO "beers" VALUES (595,'Cave Creek Chili Beer','Specialty Beer','Пиво с перцем в бутылке — это безумная, сумасшедшая идея, которая могла прийти в голову только Безумному Эду. Некоторые говорят, что идея лучше пива. Кто знает? Либо она вам нравится, либо нет.','https://brewerydb-images.s3.amazonaws.com/beer/RBnMqD/upload_HNFLdO-medium.png',4.2,NULL,1.03,1.006,0,17,1);
INSERT INTO "beers" VALUES (596,'Cave Torch','American-Style India Pale Ale','Фруктовый IPA с мандарином, щедро охмеленный сухим охмелением с использованием сортов Amarillo, Citra и Wakatu.','https://brewerydb-images.s3.amazonaws.com/beer/REqTpg/upload_CYPNZf-medium.png',6.7,50,1.06,1.012,0,22,1);
INSERT INTO "beers" VALUES (597,'CAVU','Golden or Blonde Ale','CAVU — это термин, используемый для описания идеальных условий. Мы надеемся, что у вас будет такое же представление, когда вы потягиваете этот светлый эль, который представляет собой оттенки тропических фруктов и цитрусовых от нашего хмеля западного побережья, а затем завершается чистым и ярким... как прохладный бриз в ясный летний день.','https://brewerydb-images.s3.amazonaws.com/beer/Y0VYjx/upload_rPn0Mt-medium.png',4.6,15,1.045,1.008,0,13,1);
INSERT INTO "beers" VALUES (598,'Cayman Jack Cuban Mojito','American-Style Malt Liquor','','https://brewerydb-images.s3.amazonaws.com/beer/kf1OK4/upload_PM3z9d-medium.png',5.8,12,1.05,1.004,0,10,1);
INSERT INTO "beers" VALUES (599,'Cayman Jack Margarita','American-Style Malt Liquor','','https://brewerydb-images.s3.amazonaws.com/beer/7pAdmc/upload_7tjQnm-medium.png',5.8,12,1.05,1.004,0,6,1);
INSERT INTO "beers" VALUES (600,'Cayuga Cruiser','Berliner-Style Weisse (Wheat)','Cayuga Cruiser — наш слегка терпкий и исключительно освежающий эль в стиле Berliner-Weisse. Слегка мутное на вид и легкое по телу, это пиво обеспечивает идеальное сочетание элементов, что приводит к превосходной и приятной питкости. Находитесь ли вы на лодке или на заднем дворе, это пиво — квинтэссенция летнего освежения.','https://brewerydb-images.s3.amazonaws.com/beer/L1sYZs/upload_Th22i2-medium.png',4.2,3,1.028,1.004,0,10,1);
INSERT INTO "beers" VALUES (601,'5th Anniversary Ale','Imperial or Double India Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/IDzuMf/upload_9gJOb1-medium.png',0,65,1.075,1.012,0,17,1);
INSERT INTO "beers" VALUES (602,'5th Anniversary Cinco de Drinco','Imperial or Double India Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/zVgmBl/upload_GWzcJ8-medium.png',8,65,1.075,1.012,0,28,1);
INSERT INTO "beers" VALUES (603,'5th Season','Golden or Blonde Ale','','https://brewerydb-images.s3.amazonaws.com/beer/tMrMWp/upload_ZWLexj-medium.png',4.5,15,1.045,1.008,0,19,1);
INSERT INTO "beers" VALUES (604,'5th Voyage','Brown Porter','Фантастический, хорошо сбалансированный кокосовый портер, который передает мягкую сладость кокоса, перетекающую в насыщенную прожаренность традиционного портера. Это фантастический пример все более популярного кокосового эля.','https://brewerydb-images.s3.amazonaws.com/beer/T31Hp0/upload_QQsD0Z-medium.png',6.3,20,1.04,1.006,0,2,1);
INSERT INTO "beers" VALUES (605,'6 Fifths Dessert Barleywine','American-Style Barley Wine Ale','Вы когда-нибудь пробовали 6 квинтов? Знаете этот момент, когда вы выходите за рамки, выходите за рамки, выходите за рамки и так далее? То, что вы держите в руке, — это пиво с завышенным мнением о себе. Большая крепкая бутылка роскоши. Этот эпичный барливайн провел не менее 18 месяцев в бочках из-под виски, что позволило ему выразить гигантский шквал вкусов. Ожидайте сладковатый солодовый привкус, ириски, сухофрукты, бочковой уголь, ваниль и много дуба. Лучше всего смаковать в свете деревянного камина, завернувшись в свою лучшую смокинг и утонув в своем самом удобном кожаном кресле, окруженном богатыми книгами из красного дерева и кожи, положив ноги на изголовье коврика из медвежьей шкуры. Сочетайте его с любимым вонючим сыром, декадентским десертом или мороженым-флоутером… 6 квинтов уже?','https://brewerydb-images.s3.amazonaws.com/beer/wc1PFQ/upload_gEouYY-medium.png',14.6,60,1.09,1.024,0,5,1);
INSERT INTO "beers" VALUES (606,'6 Geese-A-Laying','Belgian-Style Dark Strong Ale','6 Geese-A-Laying — это 6-е пиво в нашей серии «12 дней Рождества», возвращающее нас к более классическому темному и поджаристому зимнему элю, последующему за соответствующим образом светлым 5 Golden Rings. Сваренный с крыжовником, этот солодовый эль демонстрирует ноты сливы, темной вишни и яркие цитрусовые ароматы одноименных ягод. Вкусно прямо сейчас, но подходит для выдержки до 6 лет после выпуска 12 Drummers Drumming.','https://brewerydb-images.s3.amazonaws.com/beer/TC5N4e/upload_Ad4atF-medium.png',11.5,20,1.064,1.012,0,29,1);
INSERT INTO "beers" VALUES (607,'6 Hop IPA','American-Style India Pale Ale','В смелом сотрудничестве с Матерью-природой мы выращиваем собственные сорта ароматного хмеля на ферме Rogue Farms в Индепенденсе, штат Орегон. Мы также нанизываем, подвязываем, тренируем, тестируем, обрезаем, разделяем, сортируем, сушим и охлаждаем их. Пивовар Джон Майер тщательно отобрал и смешал 6 сортов нашего хмеля, чтобы создать мощный, прекрасный вкус нашего 6 Hop IPA.','https://brewerydb-images.s3.amazonaws.com/beer/ZxtYlW/upload_ib8KO5-medium.png',6.66,50,1.06,1.012,0,10,1);
INSERT INTO "beers" VALUES (608,'6 Pairs Of Legs','American-Style Imperial Porter','','https://brewerydb-images.s3.amazonaws.com/beer/6h0K9m/upload_anGuxF-medium.png',8.25,35,1.08,1.02,0,10,1);
INSERT INTO "beers" VALUES (609,'6 Up','American-Style Sour Ale','Лимонно-лаймовый сауэр','https://brewerydb-images.s3.amazonaws.com/beer/XfubPH/upload_hp3heb-medium.png',4.2,NULL,NULL,NULL,0,5,1);
INSERT INTO "beers" VALUES (610,'60 Below Rye IPA','American-Style India Pale Ale','Этот крепкий ржаной IPA использует экспериментальные сорта хмеля, которые придают ноты засахаренного винограда, апельсина и ванильного крема. Карри и ржаные хлопья придают глубокий красный оттенок и пряные солодовые ноты.','https://brewerydb-images.s3.amazonaws.com/beer/wtrT9p/upload_lQK0Is-medium.png',6.5,50,1.06,1.012,0,30,1);
INSERT INTO "beers" VALUES (611,'60 Days Dry Aged Stout','Oatmeal Stout','','https://brewerydb-images.s3.amazonaws.com/beer/TGlWU4/upload_DkxW63-medium.png',6,20,1.038,1.008,0,4,1);
INSERT INTO "beers" VALUES (612,'60 Minute IPA','American-Style India Pale Ale','Наш 60 Minute IPA постоянно охмеляется — более 60 добавлений хмеля за 60 минут кипячения (уже догадываетесь, откуда взялось название?). 60 Minute — это сессионный индийский светлый эль, сваренный с множеством великолепных хмелей Северо-Запада. Мощный, но сбалансированный IPA Восточного побережья с большим количеством цитрусовых хмелевых нот. Сессионное пиво для хардкорных энтузиастов пива!','https://brewerydb-images.s3.amazonaws.com/beer/TACnR2/upload_otzROk-medium.png',6,50,1.06,1.012,0,4,1);
INSERT INTO "beers" VALUES (613,'605 Harvest Pale Ale','American-Style Pale Ale','Сварено с использованием хмеля, выращенного в Южной Дакоте.','https://brewerydb-images.s3.amazonaws.com/beer/tomwG8/upload_kSOeSa-medium.png',6.1,30,1.044,1.008,0,13,1);
INSERT INTO "beers" VALUES (614,'606','English-Style India Pale Ale','Наше самое популярное пиво — английский IPA с добавлением американского хмеля.','https://brewerydb-images.s3.amazonaws.com/beer/Hq518F/upload_f16qBp-medium.png',5.4,35,1.05,1.012,0,17,1);
INSERT INTO "beers" VALUES (615,'607 Lager','Vienna-Style Lager','','https://brewerydb-images.s3.amazonaws.com/beer/hxqSyB/upload_BULGS1-medium.png',0,22,1.046,1.012,0,7,1);
INSERT INTO "beers" VALUES (616,'613 Annees','French-Style Bière de Garde','','https://brewerydb-images.s3.amazonaws.com/beer/qsMYvD/upload_YCKaGX-medium.png',0,20,1.06,1.012,0,13,1);
INSERT INTO "beers" VALUES (617,'666 Aged Ale','Wood- and Barrel-Aged Beer','','https://brewerydb-images.s3.amazonaws.com/beer/KD4ns1/upload_oWY76B-medium.png',8.6,NULL,NULL,NULL,0,17,1);
INSERT INTO "beers" VALUES (618,'667 Neighbor of the beast','American-Style India Pale Ale','Наш American India Pale Ale, который переполнен хмелевым вкусом и ароматом. Отборный американский солод и хмель придают этому пиву нотки грейпфрута и персика.','https://brewerydb-images.s3.amazonaws.com/beer/rQFFqr/upload_9pVd9M-medium.png',6.4,50,1.06,1.012,0,20,1);
INSERT INTO "beers" VALUES (619,'668 Neighbor of the Beast','Belgian-Style Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/jzZpSk/upload_gaRsZF-medium.png',8.5,20,1.044,1.008,0,23,1);
INSERT INTO "beers" VALUES (620,'67 Alaska','German-Style Kölsch / Köln-Style Kölsch','Наше фирменное пиво берет свое начало в великолепном вкусе лагеров европейского стиля. Натуральные дрожжи, местный солод и хмель обеспечивают ароматный вкус, который не будет сбивать с толку вкусовые рецепторы. Этот рецепт возник в регионе Восточная Англия в Соединенном Королевстве и был привезен через океан для вашего вкусового удовольствия. 
INSERT INTO "beers" VALUES (621,'69 on the Beach','Fruit Beer','Солнечный эль с настоящими тропическими фруктами','https://brewerydb-images.s3.amazonaws.com/beer/fm9ImO/upload_OlOdZj-medium.png',0,5,1.03,1.006,0,7,1);
INSERT INTO "beers" VALUES (622,'6:66','Imperial or Double India Pale Ale','Те, кто отваживаются пройти через самый темный час, поглощаются им. Вооруженный самыми темными солодами и самыми зловещими хмелями, этот имперский черный индийский светлый эль — идеальный порок для тех, кого тянет к тьме.','https://brewerydb-images.s3.amazonaws.com/beer/30yheM/upload_iYymbz-medium.png',0,65,1.075,1.012,0,27,1);
INSERT INTO "beers" VALUES (623,'6e Soir','American-Style Pilsener','Это пиво было специально сварено к шестой годовщине Dieu du Ciel. Оно отражает тенденцию американских пивоваров доводить все стили пива до крайностей. Поэтому этот лагер довольно крепкий по алкоголю и имеет резкую, но деликатную горечь, как и все пилснеры. Пиво полнотелое и хорошо сбалансированное благодаря тонким сладким вкусам, которые проявляются.','https://brewerydb-images.s3.amazonaws.com/beer/jddwJ0/upload_NCbjdd-medium.png',8.5,25,1.045,1.012,0,24,1);
INSERT INTO "beers" VALUES (624,'6ixth Sense Ale','Belgian-Style Pale Strong Ale','','https://brewerydb-images.s3.amazonaws.com/beer/epF2KJ/upload_BfVvLu-medium.png',8.5,20,1.064,1.012,0,23,1);
INSERT INTO "beers" VALUES (625,'6T''s Dortmunder','Dortmunder / European-Style Export','Этот немецкий лагер был впервые сварен дамами из Dragonmead. Точнее, тремя из них. Результатом стал прекрасно сбалансированный, сытный и полностью питкий немецкий лагер, который легко пьется стакан за стаканом. Это пиво производится из зерен Pils, Munich и Caramunich с добавлением только благородного хмеля Hallertauer для чистого послевкусия.','https://brewerydb-images.s3.amazonaws.com/beer/aCTXq1/upload_thpW1u-medium.png',5.3,23,1.048,1.01,0,4,1);
INSERT INTO "beers" VALUES (626,'6th Anniversary Barrel-aged Rye Wine','Wood- and Barrel-Aged Strong Beer','Это выдержанное в бочках ржаное вино было сварено в честь шестой годовщины нашего пивоварения. Каждый из трех наших пивоваров варил свой собственный рецепт, затем смешивал их в одном и том же бродильном чане. Затем мы выдерживали пиво в смеси бочек из-под бурбона и ржаного виски. Конечный результат — уникальный пряный, сложный эль с сильным влиянием бочки.','https://brewerydb-images.s3.amazonaws.com/beer/Pk65pB/upload_pXD1io-medium.png',14,NULL,NULL,NULL,0,22,1);
INSERT INTO "beers" VALUES (627,'6th Seal','Sweet or Cream Stout','Насыщенный стаут ​​с яркими акцентами шоколада, усиленный какао-бобами. Насыщенный и манящий с нотками ириски и кофе. 
INSERT INTO "beers" VALUES (628,'6v6 LupuLager','American-Style Lager','Второе пиво в нашей серии хмельных лагеров — это ода новому европейскому хмелю с привкусом белого винограда и сырными нотками в послевкусии.','https://brewerydb-images.s3.amazonaws.com/beer/HDsFQD/upload_oztaqA-medium.png',6.6,5,1.04,1.006,0,25,1);
INSERT INTO "beers" VALUES (629,'7','Belgian-Style White (or Wit) / Belgian-Style Wheat','Часть Magic Numbers, сказочной серии мелкосерийного пива: 7 — кислый витбир, сваренный с ананасом и собранной вручную мятой.','https://brewerydb-images.s3.amazonaws.com/beer/2rBIQL/upload_uQBVQY-medium.png',4.5,10,1.044,1.006,0,2,1);
INSERT INTO "beers" VALUES (630,'7 AM','Oatmeal Stout','','https://brewerydb-images.s3.amazonaws.com/beer/lJeNUG/upload_qQD95E-medium.png',7.5,20,1.038,1.008,0,13,1);
INSERT INTO "beers" VALUES (631,'7 Century','Imperial or Double India Pale Ale','Первоначально сваренный в честь первого дня рождения 7 cent. 10,5% монстр Triple IPA с обдирающим язык, возможно, совершенно излишним, 700 теоретическими IBU! Это не празднование хмеля. Это гедонистическая оргия хмеля! Мы ни за что не извиняемся!','https://brewerydb-images.s3.amazonaws.com/beer/ot0qVa/upload_NhgyK5-medium.png',10.5,65,1.075,1.012,0,12,1);
INSERT INTO "beers" VALUES (632,'7 Cities Pilsner','American-Style Pilsener','Seven Cities Pilsner, нефильтрованный немецкий Pilsner, — первое пиво в стиле лагер, выпущенное Jackalope Brewing Company. 
INSERT INTO "beers" VALUES (633,'7 Headed Monster','American-Style India Pale Ale','Мощный хмелевой аромат и вкус — вот единственная цель при разработке этого IPA в стиле Западного побережья. Более 1 фунта/баррель хмеля Simcoe и Citra при кипячении и более 2 фунтов/баррель в нашем процессе сухого охмеления с рециркуляцией хмеля придают этому пиву серьезную репутацию на улице. После укуса жертвы страдают от сильного перма-грина, хронического хайфайвита и эпического хмельного опьянения. Желаем вам отличного Uber.','https://brewerydb-images.s3.amazonaws.com/beer/SKnXqn/upload_xHWHWe-medium.png',7,50,1.06,1.012,0,17,1);
INSERT INTO "beers" VALUES (634,'7 Hills IPA','American-Style India Pale Ale','Наш первый официальный взгляд на настоящий IPA с IBU 85. В этом пиве есть хмели Mosaic, Amarillo и Citra, которые придают приятные цитрусовые нотки. Мы старались оставаться верными нашим немецким корням, думали, что пшеница дает отличное ощущение во рту. Расскажите, как у нас получилось. 
INSERT INTO "beers" VALUES (635,'7 Hop','American-Style India Pale Ale','Это секретный рецепт Джона Майера, который смешивает все семь сортов хмеля, выращенных на хмельнике Rogue Farms. Это настоящий вкус терруара апелласьона Wigrich, старейшего региона выращивания хмеля в Орегоне.','https://brewerydb-images.s3.amazonaws.com/beer/W93OE7/upload_7W9SWx-medium.png',7.77,50,1.06,1.012,0,9,1);
INSERT INTO "beers" VALUES (636,'7 Suns Double IPA','Imperial or Double India Pale Ale','Вы просили! Вы получили это... супер-большой горький 
INSERT INTO "beers" VALUES (637,'7 Swans-A-Swimming','Belgian-Style Quadrupel','Восхитительный солодовый квадрюпель в бельгийском стиле. Ноты карамели, изюма и фруктовых дрожжей.','https://brewerydb-images.s3.amazonaws.com/beer/TYV2z6/upload_sX6lRe-medium.png',11,25,1.084,1.014,0,11,1);
INSERT INTO "beers" VALUES (638,'70K','Sweet or Cream Stout','Взяв крутое и удвоив его, можно получить только вдвойне крутое. Это математика. 
INSERT INTO "beers" VALUES (639,'72 Eldorado Ale','Specialty Beer','','https://brewerydb-images.s3.amazonaws.com/beer/WrRWfX/upload_jhw0fH-medium.png',0,NULL,1.03,1.006,0,20,1);
INSERT INTO "beers" VALUES (640,'72 Imperial','Specialty Stouts','Просто расслабьтесь и наслаждайтесь поездкой. 
INSERT INTO "beers" VALUES (641,'72 Stretch','Contemporary Gose','72 Stretch — это чистый ферментированный эль в стиле гозе. Солодовая засыпь состоит из солода пилснер и 35% пшеницы с добавлением подкисленного и мюнхенского солода. Мы используем хмель Herkules, Perle и Summer для создания легкой горечи и приятного аромата хмеля. Мы также добавляем в котел средиземноморскую соль и кориандр для создания классических характеристик гозе. Мы сбраживаем пиво чистым штаммом эля, а затем охмеляем его сухим способом с помощью лимонной и лаймовой цедры. В результате получается освежающий, цитрусовый эль с нотками дыни и косточковых фруктов.','https://brewerydb-images.s3.amazonaws.com/beer/BIHt0i/upload_H9DYLs-medium.png',4.6,NULL,NULL,NULL,0,15,1);
INSERT INTO "beers" VALUES (642,'73 Kolsch','German-Style Kölsch / Köln-Style Kölsch','Надежно свежий и мягкий, с последовательными оттенками землистых хмелевых ароматов и легкой ржаной горечью.','https://brewerydb-images.s3.amazonaws.com/beer/UiwaFo/upload_0KYNW2-medium.png',5.7,18,1.042,1.006,0,23,1);
INSERT INTO "beers" VALUES (643,'75 Minute IPA','American-Style India Pale Ale','Смешайте два любимых сорта Dogfish Head, добавьте кленовый сироп и естественным образом газируйте смесь. Что вы получите? 75 Minute IPA. 
INSERT INTO "beers" VALUES (644,'75th Anniversary Ale','American-Style Pale Ale','Вест-Пойнт Пэйл Эль','https://brewerydb-images.s3.amazonaws.com/beer/Xl1rDs/upload_YG0sUa-medium.png',5.2,30,1.044,1.008,0,20,1);
INSERT INTO "beers" VALUES (645,'77 Fremont Select','Session India Pale Ale','Свежее и освежающее весеннее вундербир 77 Select производится из 7 отборных ингредиентов и варится на огне в пивоварнях Fremont Brewing, чтобы достичь своего изысканного статуса Session IPA. Этим пивом мы отдаем дань уважения старым пивоваренным компаниям Сиэтла Horlucks & Sicks Brewing, предшественникам Rainier Brewing, и их флагманским сортам пива 66 Select и Rainier. «Алкоголь может и не решить ваши проблемы, но вода или молоко тоже». - VWP','https://brewerydb-images.s3.amazonaws.com/beer/2qGII9/upload_3BKKlZ-medium.png',4,NULL,NULL,NULL,0,8,1);
INSERT INTO "beers" VALUES (646,'77 Lager','German-Style Pilsener','Лагер, который на самом деле имеет вкус чего-то? 
INSERT INTO "beers" VALUES (647,'7K IPA','American-Style India Pale Ale','7K IPA варится в Санта-Фе, штат Нью-Мексико, и представляет собой сухой IPA в стиле Западного побережья с нотками грейпфрута, цитрусовых и тропических ароматов. Хмелевой профиль сочетает Simcoe и Mosaic с Eldorado и Amarillo в качестве резерва. На вкус он начинается с легкой карамельной солодовой сладости, которая уравновешивает хмель, и заканчивается фруктовым, слегка смолистым хмелевым вкусом и чистой горечью. Он фруктовый и интенсивный с чистым хмелевым профилем и более легкой солодовой основой, чем другие IPA. Так что если вы ищете следующий отличный IPA, просто посмотрите вверх. Выше. Нет, выше. Вот мы.','https://brewerydb-images.s3.amazonaws.com/beer/enwmqm/upload_93AZFa-medium.png',7,50,1.06,1.012,0,22,1);
INSERT INTO "beers" VALUES (648,'7th Anniversary Ale','Specialty Beer','','https://brewerydb-images.s3.amazonaws.com/beer/grBMfJ/upload_vcNpRO-medium.png',10,NULL,1.03,1.006,0,16,1);
INSERT INTO "beers" VALUES (649,'7th Street Wheat','Light American Wheat Ale or Lager without Yeast','«7th Street Wheat» начинался как наш летний сезонный напиток, но из-за своей популярности занял свое место в наших круглогодичных элях. Названный в честь пристани 7th Street через дорогу от пивоварни, этот фильтрованный пшеничный эль имеет легкое тело в американском стиле с фунтами свежего лимонного базилика, добавленного после ферментации. Легкий цитрусовый вкус с ноткой пряной травы на заднем плане хорошо сочетается практически с любым блюдом из морепродуктов, и, насколько мы слышали, является фаворитом в барах как отдельное освежающее пиво.','https://brewerydb-images.s3.amazonaws.com/beer/YNP1P7/upload_j9jiX0-medium.png',4.5,10,1.036,1.004,0,23,1);
INSERT INTO "beers" VALUES (650,'8 Ball Rye IPA','American-Style India Pale Ale','Сухая пустыня Невада! бесконечные виды. Американо. Этот напиток — дань уважения крупным IPA из США. Большая часть ржи используется для придания пиву пряного, землистого привкуса, смешанного с солодом Кара для создания насыщенного, смелого вкуса. Пикантный хмель из Южного полушария и Америки используется в изобилии для баланса остаточного сладкого солодового характера. Массы сухого хмеля добавляются после ферментации, чтобы придать дикую смесь тропических, пряных ароматов.','https://brewerydb-images.s3.amazonaws.com/beer/Jra85l/upload_f6uKCT-medium.png',6.2,50,1.06,1.012,0,23,1);
INSERT INTO "beers" VALUES (651,'Cayuse Creek Common','California Common Beer','История: 
INSERT INTO "beers" VALUES (652,'Caywood Station Oatmeal Stout','Oatmeal Stout','Классический британский овсяный стаут. Темные, насыщенные солодовые тона подчеркиваются тонкой сладостью свежего цельного овса. В этом сливочном, насыщенном вкусовом профиле преобладают ноты шоколада, кофе и карамели. Этот сложный, но элегантный британский эль чрезвычайно мягкий и питкий.','https://brewerydb-images.s3.amazonaws.com/beer/dWrNma/upload_2ifxRq-medium.png',5.5,20,1.038,1.008,0,5,1);
INSERT INTO "beers" VALUES (653,'CB''s MacBubba','Scottish-Style Light Ale','Познакомьтесь с «MacBubba». Этот БОЛЬШОЙ, старый добрый парень — шотландец до мозга костей. Он смелый, но абсолютно милый. Как только вы узнаете MacBubba, вы не будете знать, как вы вообще обходились без него. Этот шотландский эль имеет тонкий дымный оттенок, которого нет во многих других сортах пива. Начинаясь с красивого темно-янтарного цвета, он имеет приятный насыщенный аромат, который следует за полнотелым карамельным вкусом. Вы заметите легкий «торфяной» характер в финале, полученный благодаря добавлению лучшего торфяного солода из Шотландии. Мы думаем, что вы найдете этот шотландский эль очень легким для наслаждения, и в то же время достаточно крепким, чтобы выдержать суровый зимний холод.','https://brewerydb-images.s3.amazonaws.com/beer/7nI490/upload_Qf4Ohy-medium.png',6.5,9,1.03,1.006,0,28,1);
INSERT INTO "beers" VALUES (654,'Ceci N''est Pas A','Imperial or Double India Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/qD1XTG/upload_Rtw9Mw-medium.png',8.5,65,1.075,1.012,0,26,1);
INSERT INTO "beers" VALUES (655,'Ceci N’est Pas Mild Ale','Belgian-Style Pale Ale','Дикий бельгийский кузен моего «мягкого эля». Тот же рецепт солода и хмеля, но с комбинацией бельгийских и местных диких дрожжей. Для последнего я открываю ферментер ночью, чтобы позволить диким дрожжам сделать свое дело с суслом, которое ферментируется в пиво. 
INSERT INTO "beers" VALUES (656,'Cedar Plank Pale Ale','American-Style Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/2I9LpS/upload_IqGad4-medium.png',6,30,1.044,1.008,0,28,1);
INSERT INTO "beers" VALUES (657,'Cedar Point Amber Ale','American-Style Amber/Red Ale','Наслаждайтесь этим новым взглядом на старый фаворит. Наш немного более охмеленный янтарный эль привносит неожиданный привкус в классический янтарный вкус. Янтарные эли являются распространенным фаворитом Висконсина; известны своей «правильной» консистенцией и вкусом. Названы в честь кедровых берегов на северной стороне Женевского озера; вид не будет единственным, что заставит вас вернуться за добавкой.','https://brewerydb-images.s3.amazonaws.com/beer/RbZY1d/upload_8Z2aah-medium.png',0,30,1.048,1.012,0,27,1);
INSERT INTO "beers" VALUES (658,'Cedar Point Shandy Shores','Fruit Beer','Яркий и солнечный напиток для утоления жажды с настоящим красным апельсином для терпкого цитрусового вкуса и ноткой настоящего манго для тропического оттенка.','https://brewerydb-images.s3.amazonaws.com/beer/kypAcX/upload_7L4B6s-medium.png',4.5,5,1.03,1.006,0,26,1);
INSERT INTO "beers" VALUES (659,'Cedric''s Brown Ale','American-Style Brown Ale','Большой и дружелюбный, наш Brown Ale так же доступен, как и его вдохновитель — Седрик, любимый сенбернар Джорджа Вандербильта. Наше пиво глубокого красновато-коричневого цвета предлагает нотки карамели и жареного ячменя с ореховым привкусом, создаваемым солодом Victory ™. Четкое послевкусие отмечено хорошо сбалансированным хмелем.','https://brewerydb-images.s3.amazonaws.com/beer/wIGCKB/upload_bLjh6f-medium.png',0,25,1.04,1.01,0,28,1);
INSERT INTO "beers" VALUES (660,'Cedric''s Pale Ale','American-Style Pale Ale','Этот эль такой же резвый, как и его тезка Седрик, патриарх сенбернаров, который делил Biltmore House с Вандербильтами. Высококачественное зерно и английские сорта хмеля придают нашему полнотелому элю нежный соломенный цвет с солодовыми и фруктовыми ароматами. Менее горький, чем большинство светлых элей, но все же сохраняет характерное длительное хмелевое послевкусие.','https://brewerydb-images.s3.amazonaws.com/beer/JyXOAk/upload_11iUnk-medium.png',0,30,1.044,1.008,0,5,1);
INSERT INTO "beers" VALUES (661,'Ceili Irish Red Ale','Irish-Style Red Ale','Позвольте представить вам CEiLí, мягкий и легко пьющийся традиционный ирландский красный эль. Названный в честь ирландского слова, означающего «вечеринка», CEiLí появляется как раз к весне. Слегка сладкий и слегка охмеленный, нотка жареного ячменя придает CEiLí янтарный оттенок и несколько суховатое послевкусие. Двухрядный солод Maris Otter и пониженная карбонизация придают округлость вкуса и расслабленную пену. Пусть начнется CEiLí!','https://brewerydb-images.s3.amazonaws.com/beer/KmaXDc/upload_UPqOBo-medium.png',5,20,1.04,1.01,0,21,1);
INSERT INTO "beers" VALUES (662,'Cèilidh','Scottish-Style Heavy Ale','Wee Heavy в темно-коричневом стиле с отблесками рубина, комплексами карамельных сухариков, эко-карамелью и фруктовыми конфи. 
INSERT INTO "beers" VALUES (663,'Celastrina','French & Belgian-Style Saison','','https://brewerydb-images.s3.amazonaws.com/beer/bNxnMb/upload_E4evpE-medium.png',7.5,20,1.055,1.004,0,1,1);
INSERT INTO "beers" VALUES (664,'Celebración','Belgian-Style Dark Strong Ale','Этот темный бельгийский крепкий эль сварен с мыслью о праздничном сезоне. Сочетание жареного солода и бисквитного солода придает теплый хлебный солодовый профиль, а свежий имбирь добавляет праздничную пряность.','https://brewerydb-images.s3.amazonaws.com/beer/RNF6An/upload_LxHBn9-medium.png',8,20,1.064,1.012,0,2,1);
INSERT INTO "beers" VALUES (665,'Celebration','Golden or Blonde Ale','По случаю нашего 5-летнего юбилея в 2010 году мы выпустили новое пиво. 
INSERT INTO "beers" VALUES (666,'Celebration Ale','Fresh "Wet" Hop Ale','Длинные, холодные зимние ночи немного ярче с Celebration® Ale. Удивительно крепкий и насыщенный, Celebration® Ale охмелен сухим способом для живого, интенсивного аромата. Сваренный специально для праздников, он идеально подходит для праздничной встречи или для тихого вечера дома.','https://brewerydb-images.s3.amazonaws.com/beer/KY5gtZ/upload_Ymm2uC-medium.png',6.8,NULL,NULL,NULL,0,20,1);
INSERT INTO "beers" VALUES (667,'Celebration!','American-Style Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/N53HKZ/upload_hm3ktJ-medium.png',6.5,30,1.044,1.008,0,29,1);
INSERT INTO "beers" VALUES (668,'Celebrator','German-Style Doppelbock','Пиво с доминирующим солодовым вкусом. Происхождение этого пива по рецепту монаха отражается в его крепости. Папа пива Конрад Зайдль описывает его так: «Почти черное с очень легким красным оттенком, сенсационная, праздничная пена и действительно необычный аромат, который поначалу вызывает видения шкварок. Первый вкус имеет легкую полноту с сопутствующим кофейным тоном, который становится более доминирующим с послевкусием. В нем очень мало сладости, которую часто можно почувствовать в пиве доппельбок». Ayinger Celebrator несколько раз был признан одним из лучших сортов пива мира Чикагским институтом тестирования и завоевал множество платиновых медальонов.','https://brewerydb-images.s3.amazonaws.com/beer/Z8eagB/upload_xzaNCN-medium.png',6.7,17,1.074,1.014,0,17,1);
INSERT INTO "beers" VALUES (669,'Celebrator Beer News','American-Style Strong Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/v5a7l8/upload_MEKRqu-medium.png',9.3,40,1.05,1.008,0,6,1);
INSERT INTO "beers" VALUES (670,'Celestial','Common Cider','Этот сидр имеет крепкую структуру танинов, которая демонстрирует ноты землистости, пряностей и цитрусовых. Это происходит из смеси яблок Ellis Bitter и Albemarle Pippin. Послевкусие Celestial усиливается нотками яблок Winesap, которые обеспечивают яркое чистое послевкусие. Сочетание этого сидра с блюдами, в состав которых входит розмарин или лаванда, подчеркнет его ароматические ноты.','https://brewerydb-images.s3.amazonaws.com/beer/JWkvE2/upload_qrAjIE-medium.png',8.1,NULL,1.045,1,0,12,1);
INSERT INTO "beers" VALUES (671,'Celestial CDL','American-Style Dark Lager','Фруктовый, цветочный хмель встречается с бархатистым, темным солодом в этом пиве Meridian Expedition. Наш Cascadian Dark Lager обеспечивает множество цитрусовых, тропических фруктов и аромата сосны благодаря добавлению хмеля Meridian при выдержке и в лагерном танке. Легкая сладость и поджаренный, жареный солод создают противовес солидной хмелевой горечи. Для пива такого темного цвета Celestial CDL заканчивается на удивление легко и чисто благодаря своему характеру брожения и лагерирования.','https://brewerydb-images.s3.amazonaws.com/beer/0zkPb7/upload_iKeMOz-medium.png',5.1,14,1.04,1.008,0,4,1);
INSERT INTO "beers" VALUES (672,'Celestial Dawn Speedway Stout','Specialty Stouts','Каждый квартал мы напрягаем свои творческие мускулы и варим вариацию нашего круглогодичного Imperial Stout. В этом квартале мы объединились с нашими хорошими друзьями из Modern Times Coffee, чтобы создать особую пару кофе и пива. Их кофе Celestial Dawn имеет крепкие ноты шоколада, ягод, косточковых фруктов, древесины и специй. В сочетании с нашим Speedway Stout смесь полна шоколадных, фруктовых и карамельных нот.','https://brewerydb-images.s3.amazonaws.com/beer/j1ObDk/upload_3bSWdH-medium.png',12,35,1.08,1.02,0,12,1);
INSERT INTO "beers" VALUES (673,'Celestis Thesauri','French-Style Bière de Garde','Наш biere de garde — это элегантное выражение этого проверенного временем деревенского фермерского эля. Его аромат и вкус прекрасного янтарного цвета представляют собой сложное сочетание сладкого солода, темных фруктов и землистости. Традиционная выдержка приводит к хорошо сбалансированному элю с немного сладковатым послевкусием. Крепкий и удовлетворяющий эль.','https://brewerydb-images.s3.amazonaws.com/beer/aNQCjH/upload_0ISW6u-medium.png',8.3,20,1.06,1.012,0,13,1);
INSERT INTO "beers" VALUES (674,'Celia Saison','French & Belgian-Style Saison','Celia Saison вдохновлен деревенскими фермерскими элями Бельгии. Пряный вкус сорго дополняется уникальными вкусами нашего бельгийского штамма дрожжей, а также добавлением апельсиновой цедры Кюрасао и хмеля Celeia. Приглашаем вас попробовать наше восхитительное предложение без глютена. 
INSERT INTO "beers" VALUES (675,'Celilo CDA','American-Style Black Ale','Тёмный эль, охмелённый сортами Summit, Chinook и Tomahawk, с солодовой основой Midnight Wheat.','https://brewerydb-images.s3.amazonaws.com/beer/yHZLMa/upload_thsHrg-medium.png',7.6,50,1.056,1.012,0,1,1);
INSERT INTO "beers" VALUES (676,'Celis Pale Bock','Other Belgian-Style Ales','Это эклектичное пиво имеет янтарный цвет и теплый насыщенный вкус, который сохраняется надолго. Оно сварено из карамельного солода с хмелями Saaz, Willamette и Cascade. Этот бельгийский Bock такой же насыщенный, теплый и уникальный, как Пьер Селис, человек, который его впервые приготовил.','https://brewerydb-images.s3.amazonaws.com/beer/Xc6skp/upload_4PqkXq-medium.png',4.9,NULL,NULL,NULL,0,26,1);
INSERT INTO "beers" VALUES (677,'Cellar 70 - Drift Wood Ale','Specialty Beer','Самые лучшие вещи в жизни часто находятся прямо перед нами.','https://brewerydb-images.s3.amazonaws.com/beer/q0Dad0/upload_R6pmSa-medium.png',5.2,NULL,1.03,1.006,0,11,1);
INSERT INTO "beers" VALUES (678,'Cellar Blender Sour Ale','American-Style Sour Ale','Эль, сваренный с клюквой, вишней и малиной. Этот темный эль ферментируется с различными фруктами, затем смешивается с выдержанным в дереве кислым пивом для получения сложных нот кислой вишни и средней полноты. Наслаждайтесь им в чистом виде или в смеси с рецептами, предложенными нашими пивоварами.','https://brewerydb-images.s3.amazonaws.com/beer/q8UUqR/upload_NsDm0p-medium.png',7.5,NULL,NULL,NULL,0,13,1);
INSERT INTO "beers" VALUES (679,'Cellar Dark Ale','Specialty Beer','','https://brewerydb-images.s3.amazonaws.com/beer/aUOkUs/upload_xF1b8S-medium.png',0,NULL,1.03,1.006,0,9,1);
INSERT INTO "beers" VALUES (680,'Cellar Door','Wood- and Barrel-Aged Beer','На протяжении веков термин «cellar door» неоднократно упоминался как самый красивый термин в английском языке. Когда я приступил к созданию первого летнего дополнения к линейке элей в США, меня почти сразу же посетило чувство красоты и очищения. Многим летним предложениям, как правило, не хватает сложности их более крупных, более холодных аналогов; поэтому моей целью было создать эль с экстремальным балансом и деликатной сложностью, который позволяет созерцать, а также обеспечивает освежающую выпивку. Начав с базы немецкой пшеницы и светлого солода, эта хрустящая, слегка мутная основа была затем подчеркнута смесью хмеля Sterling & Citra, обеспечивающей сложную смесь травяных трав и мандариновых цитрусовых вкусов и ароматов. Чтобы собрать все это воедино и завершить «очищающий» аспект моего видения, я аккуратно завершил эль каплей белого шалфея, придав смеси мягкий землистый пряный характер. Конечно, не стоит забывать о наших домашних дрожжах для сэзона, которые объединили все элементы, оставив сухое, но изысканное послевкусие.','https://brewerydb-images.s3.amazonaws.com/beer/otXCdW/upload_quKqLB-medium.png',6.8,NULL,NULL,NULL,0,8,1);
INSERT INTO "beers" VALUES (681,'Cellarman''s Reserve','American-Style India Pale Ale','Классический американский IPA хорош в любое время года, и именно поэтому мы рады представить вам нашу серию Cellarman’s Reserve Single Hop IPA, доступную круглый год. Чтобы подчеркнуть хмель, в основу пива положен простой рецепт, состоящий только из двухрядного и мюнхенского солода. 7% abv дает нам как раз нужный баланс для 65 IBU хмелевого совершенства, получаемого за счет использования только одного сорта хмеля для горечи, вкуса и сухого охмеления. Далее следует твист Martin House. Хотя он доступен круглый год, хмель, который мы используем, будет доступен не всегда. Время от времени, когда нашим Cellarman захочется, мы заменим этот сорт хмеля. Хотя пиво в целом останется прежним, пьющий сможет ощутить нюансы каждого сорта хмеля.','https://brewerydb-images.s3.amazonaws.com/beer/xrJNQS/upload_DgMyJu-medium.png',7,50,1.06,1.012,0,23,1);
INSERT INTO "beers" VALUES (682,'Celt''s Golden Ale','Golden or Blonde Ale','Бледно-золотистого цвета, сладкое и фруктовое, с легкой сухостью и хмельным послевкусием. Этот California Golden Ale — идеальное пиво для «сессионного» употребления — превосходный эль, который подойдет для любого случая.','https://brewerydb-images.s3.amazonaws.com/beer/qDaSyQ/upload_dgreDz-medium.png',5,15,1.045,1.008,0,5,1);
INSERT INTO "beers" VALUES (683,'Celtic Ale','Irish-Style Red Ale','','https://brewerydb-images.s3.amazonaws.com/beer/QxzH7J/upload_xqRty1-medium.png',5.5,20,1.04,1.01,0,14,1);
INSERT INTO "beers" VALUES (684,'Celtic Fury','Classic Irish-Style Dry Stout','Впервые сваренный в 2005 году, этот традиционный ирландский стаут ​​успокаивает гнев кланов своим восхитительным ароматом жареного кофе, мягким, сливочным вкусом и изысканно сухим послевкусием с тонкими нотками кофе и шоколада. Поднимите бутылку или пинту этого крепкого, глубокого гранатового шедевра вместо рук, и все обиды будут прощены.','https://brewerydb-images.s3.amazonaws.com/beer/XViYB0/upload_aflEl3-medium.png',5,30,1.038,1.008,0,11,1);
INSERT INTO "beers" VALUES (685,'Celtic Red','Irish-Style Red Ale','Один из обладателей медали домашнего пивоварения на Ярмарке штата Калифорния, этот прекрасный рубиново-красный эль отличается крепостью, сложностью и полнотой вкуса с легким оттенком благородного хмеля. Sláinte!','https://brewerydb-images.s3.amazonaws.com/beer/beu8pQ/upload_Jc9WMN-medium.png',6.3,20,1.04,1.01,0,26,1);
INSERT INTO "beers" VALUES (686,'Celtic Rose','Irish-Style Red Ale','Наша версия традиционного ирландского янтарного эля. Это пиво сочетает в себе богатство немецкого и австрийского солода с нежным и пряным британским хмелем, создавая вкус, достойный Города Красной Розы.','https://brewerydb-images.s3.amazonaws.com/beer/Z48pjy/upload_TrcjVm-medium.png',5,20,1.04,1.01,0,3,1);
INSERT INTO "beers" VALUES (687,'Celtic Stout','American-Style Stout','Родственники Дэна из старой деревни с гордостью дали бы ему пинту его собственного кельтского стаута. Этот крепкий и сытный стаут ​​сварен из 100% чистого английского солода, лучшего солода для стаута в мире. Глубокие шоколадные тона солода Maris Otter подчеркиваются редким хмелем старого света Alsatian Strisselspalter. 
INSERT INTO "beers" VALUES (688,'Censored Rich Copper Ale','American-Style Amber/Red Ale','Первоначальное название оригинального эля изначально произошло от происхождения, настолько отвратительного, что мы не можем раскрыть его исконное происхождение. Извините. На самом деле это легко пьющийся солодовый медный эль, который легко усваивается хмелем. Как бы то ни было.','https://brewerydb-images.s3.amazonaws.com/beer/GboTWT/upload_mnGRl3-medium.png',6.75,30,1.048,1.012,0,2,1);
INSERT INTO "beers" VALUES (689,'Centenni Ale Blonde Ale','Golden or Blonde Ale','','https://brewerydb-images.s3.amazonaws.com/beer/53RO7I/upload_kFz4OU-medium.png',0,15,1.045,1.008,0,30,1);
INSERT INTO "beers" VALUES (690,'Centenni Ale Dark Ale','Specialty Beer','','https://brewerydb-images.s3.amazonaws.com/beer/hNACGj/upload_97Dph8-medium.png',0,NULL,1.03,1.006,0,17,1);
INSERT INTO "beers" VALUES (691,'Centennial','American-Style Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/k6UWAV/upload_Cn5PKh-medium.png',0,30,1.044,1.008,0,15,1);
INSERT INTO "beers" VALUES (692,'Centennial','Imperial or Double India Pale Ale','Когда ниндзя отдыхают от своих битв с мифическим единорогом, они действительно принимают свой внутренний хмельной мозг. Этот конкретный ниндзя выбрал путь хмеля Centennial. Полностью загруженный хмелем Centennial, этот имперский IPA наполнен сочным хмелевым характером от носа до неба. Насладитесь отдыхом от собственных жизненных битв с ниндзя Centennial.','https://brewerydb-images.s3.amazonaws.com/beer/8bMUsU/upload_YOGWAp-medium.png',9.5,65,1.075,1.012,0,11,1);
INSERT INTO "beers" VALUES (693,'Centennial Ale','Scotch Ale','Этот шотландский эль солодовый, янтарного цвета. Centennial Ale был сварен в честь 100-летия пожарных, защищающих общину Ломбардии.','https://brewerydb-images.s3.amazonaws.com/beer/nixxF8/upload_5s9ixP-medium.png',6.5,25,1.072,1.016,0,9,1);
INSERT INTO "beers" VALUES (694,'Centennial Ale','American-Style India Pale Ale','В этом традиционном Indian Pale Ale нет ничего традиционного. Янтарного цвета с ИНТЕНСИВНОЙ солодовостью, идеально сбалансированной с пряным хмелевым вкусом. Тщательно охмелено в конце цикла ферментации хмелем Centennial. Эль, который лечит все, что делает тебя ЭЛЕМ!!!','https://brewerydb-images.s3.amazonaws.com/beer/SGbBC5/upload_T8Dx6A-medium.png',6.5,50,1.06,1.012,0,23,1);
INSERT INTO "beers" VALUES (695,'Centennial Falcon','American-Style Pale Ale','В честь Международного дня «Звездных войн» — 4 мая — каждый год мы выпускаем СОВЕРШЕННО НОВОЕ специальное ограниченное количество пива, сваренного в честь праздника. 
INSERT INTO "beers" VALUES (696,'Centennial Fresh Hop Helles Lager','Münchner (Munich)-Style Helles','','https://brewerydb-images.s3.amazonaws.com/beer/5R2t4D/upload_dNKcwR-medium.png',0,18,1.044,1.008,0,8,1);
INSERT INTO "beers" VALUES (697,'Centennial Harvest Ale','American-Style India Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/ksCaVf/upload_eLJc6j-medium.png',5.7,50,1.06,1.012,0,19,1);
INSERT INTO "beers" VALUES (698,'Centennial Hop Mountain Marzen (Batch No. 80524)','American-Style Märzen / Oktoberfest','','https://brewerydb-images.s3.amazonaws.com/beer/zd3xxc/upload_0rVCKR-medium.png',5.9,20,1.05,1.012,0,17,1);
INSERT INTO "beers" VALUES (699,'Centennial IPA','American-Style India Pale Ale','Приготовьтесь погреться в лучах славы цветочного букета пенной шапки. Насладитесь цитрусовыми акцентами от обилия сухого охмеления. Это пиво сладкое, но сбалансированное. Солодовые оттенки сочетаются с хмелевым характером для финиша, который никогда не становится слишком горьким.','https://brewerydb-images.s3.amazonaws.com/beer/rAHla4/upload_RUG7Ly-medium.png',7.2,50,1.06,1.012,0,30,1);
INSERT INTO "beers" VALUES (700,'Centennial Martyr Double IPA','Imperial or Double India Pale Ale','Очень насыщенный, цитрусовый и солодовый Imperial Double IPA, содержащий 120 фунтов клеверного меда с фермы Hunber''s Honey Farm в Мартинсвилле, штат Индиана. Он охмелен насухо хмелем Galaxy и Cascade.','https://brewerydb-images.s3.amazonaws.com/beer/jgH7bb/upload_tZjBCQ-medium.png',8.5,65,1.075,1.012,0,4,1);
INSERT INTO "beers" VALUES (701,'Centennial Pale Ale','American-Style Pale Ale','Centennial Pale Ale от Rabbit Hole Brewing — сбалансированный, пригодный для употребления американский Pale с цитрусовыми, цветочными нотками хмеля Centennial. Это пиво с одним хмелем использует смесь американского и британского солода, чтобы дополнить яркий хмелевой профиль, оставаясь при этом свежим и освежающим. Centennial Pale будет доступен только в течение ограниченного времени — после этого может пройти 100 лет, прежде чем мы снова сварим его. Не упустите!','https://brewerydb-images.s3.amazonaws.com/beer/MjlZOk/upload_lkqlX9-medium.png',5.6,30,1.044,1.008,0,6,1);
INSERT INTO "beers" VALUES (702,'Centennial Single Hop IPA','American-Style India Pale Ale','Centennial — традиционный американский сорт хмеля. Он присутствует во многих великих американских IPA, и его популярность широко распространена, оставляя большой, жирный след на пивной сцене США. Centennial ценится не без причины. Он сочетает в себе все, что хочется от хмеля: запах фруктов и цветов, вкус сосны и можжевельника, и он чертовски горький.','https://brewerydb-images.s3.amazonaws.com/beer/s5IAhv/upload_UQlNC1-medium.png',6.9,50,1.06,1.012,0,12,1);
INSERT INTO "beers" VALUES (703,'Center Line','Golden or Blonde Ale','Легко пьющийся золотистый эль, приятный на вкус для тех, кто не любит резкие, насыщенные вкусы.','https://brewerydb-images.s3.amazonaws.com/beer/9q86va/upload_Cde0AL-medium.png',5,15,1.045,1.008,0,1,1);
