INSERT INTO "beers" VALUES (706,'Centerpiece','Fruit Beer','Центральное место занимает фруктовый пирог «Midwest Fruit Tart», сваренный из 1500 фунтов ананаса и 1500 фунтов клубники на каждую партию объемом 30 баррелей.','https://brewerydb-images.s3.amazonaws.com/beer/XxnEgu/upload_e8VhDt-medium.png',7.9,5,1.03,1.006,0,2,1);
