INSERT INTO "beers" VALUES (1087,'Christmas Ale','Other Belgian-Style Ales','Этот стиль пива классифицируется в категории «бельгийское». Очевидно, что основное внимание уделяется дрожжам, используемым в процессе пивоварения. В нашу версию добавлена ​​апельсиновая цедра, чтобы подчеркнуть фруктовый вкус и аромат, получаемые от дрожжей.','https://brewerydb-images.s3.amazonaws.com/beer/MrCcER/upload_bgxtbR-medium.png',7,NULL,NULL,NULL,0,19,1);
