INSERT INTO "beers" VALUES (1109,'Chuck Morris','Imperial or Double India Pale Ale','Chuck Morris — это двойной IPA, который проложит путь к вашему сердцу с разворота, карате и пулемета. Идеально охмеленный, мощный, как его тезка, но сбалансированный, как боец, садящийся на шпагат. Обязательно сначала растянитесь!','https://brewerydb-images.s3.amazonaws.com/beer/DbAYoX/upload_qq93dZ-medium.png',8,65,1.075,1.012,0,13,1);
