INSERT INTO "beers" VALUES (1416,'Cluster Single Hop IPA','American-Style India Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/ftZ3vT/upload_fNMwlR-medium.png',0,50,1.06,1.012,0,13,1);
