INSERT INTO "beers" VALUES (2035,'Craigalicious','German-Style Schwarzbier','Благодаря солодовой засыпи, подобранной для создания легкого и свежего пива, Craigalicious — это очень приятное к употреблению пиво с цветом стаута, душой пилза и нотками жареного вкуса.','https://brewerydb-images.s3.amazonaws.com/beer/s9nve8/upload_GA5edG-medium.png',5,22,1.044,1.01,0,30,1);
INSERT INTO "beers" VALUES (2036,'Cran Apple Sour','Berliner-Style Weisse (Wheat)','Наш Kettle Soured Lager — это вариация традиционного Berliner Weisse. Сусло закисляется с использованием подкисленного солода и 3 штаммов лактобактерий в течение 36 часов. После достижения pH в котле пиво проходит традиционную ферментацию лагера. Во время ферментации мы добавляем щедрое количество яблочного сидра с северо-запада Тихого океана, в общей сложности чуть более 1 фунта на галлон пива. Затем пиво повторно ферментируется с использованием щедрого количества клюквы. Полученное пиво терпкое и свежее, но сбалансированное, и в то же время наполненное свежими ароматами яблок и клюквы.','https://brewerydb-images.s3.amazonaws.com/beer/CML5hb/upload_0ecgnb-medium.png',6.8,3,1.028,1.004,0,1,1);
