INSERT INTO "beers" VALUES (2044,'Cranberry Gose','Contemporary Gose','Сварено с клюквой с фермы Bluewater в Массачусетсе, кориандром и щепоткой соли. Результат — освежающе терпкий и игристый клюквенный коктейль, созданный для того, чтобы подпитывать вашу страсть к путешествиям, вдохновленную теплой погодой.','https://brewerydb-images.s3.amazonaws.com/beer/PzKG7b/upload_EEzmQy-medium.png',5.2,NULL,NULL,NULL,0,1,1);
