INSERT INTO "beers" VALUES (2052,'Cranberry Wit','Belgian-Style White (or Wit) / Belgian-Style Wheat','Новый взгляд на старый фаворит, Cranberry Wit! Мы начали с того, что сварили классический witbier в бельгийском стиле с кориандром и апельсиновой цедрой, к которому мы добавили сочную клюкву. Результат — живое, освежающее пиво с легкой терпкостью.','https://brewerydb-images.s3.amazonaws.com/beer/IG5Rqi/upload_uC3OZP-medium.png',5.5,10,1.044,1.006,0,29,1);
INSERT INTO "beers" VALUES (2053,'Cranberry, Plum, & Black Currant Gose','Contemporary Gose','Гозе с клюквой, сливой и черной смородиной','https://brewerydb-images.s3.amazonaws.com/beer/QZY1kx/upload_7a0Omv-medium.png',4.7,NULL,NULL,NULL,0,30,1);
INSERT INTO "beers" VALUES (2054,'Cranberry-Quince Berliner Weisse','Berliner-Style Weisse (Wheat)','Влиятельный автор пивных произведений покойный Майкл Джексон описывает «берлинское белое пиво» ​​как имеющее «настойчивую игристость, ароматную фруктовость в носу, острый, сухой вкус и дрожь утоляющей кислой кислотности в послевкусии». Третье предложение в нашей спонтанно кислой сезонной серии Berliner Weisse, North Coast Brewing Cranberry-Quince Berliner Weisse, производится из сока орегонской клюквы и грушевидной айвы — идеальное дополнение ко всем вашим осенним праздникам!','https://brewerydb-images.s3.amazonaws.com/beer/VpBkro/upload_z6DLzr-medium.png',4.1,3,1.028,1.004,0,19,1);
