INSERT INTO "beers" VALUES (2067,'Crazy Aunt','Specialty Beer','Crazy Aunt — это эль, сваренный с можжевельником, кориандром, соком лайма и тоником. Мы раздвинули границы с этим пивом и сварили его так, чтобы оно имело вкус джина с тоником, и мы представляем его как идеальный сезонный напиток, чтобы утолить жажду в теплый летний день в Миннесоте.','https://brewerydb-images.s3.amazonaws.com/beer/ydSf96/upload_bp96TO-medium.png',6,NULL,1.03,1.006,0,2,1);
