INSERT INTO "beers" VALUES (2069,'Crazy Beaver Cream Ale','American-Style Cream Ale or Lager','Большое количество кукурузных хлопьев придает этому пиву светло-соломенный цвет и чистое тело, что делает его освежающим и ароматным, его можно пить в любое время.','https://brewerydb-images.s3.amazonaws.com/beer/hP2luL/upload_bnmggM-medium.png',4.5,10,1.044,1.004,0,30,1);
