INSERT INTO "beers" VALUES (2125,'Crescent Fresh','American-Style India Pale Ale','Этот IPA наполнен американским хмелем, который добавляется на каждом этапе процесса пивоварения. Яркие ноты цитрусовых в аромате дополняются мягкой горечью и хрустящим, сосновым послевкусием. Мы добавили в это пиво чертову тонну хмеля в надежде, что мы угодим даже самым придирчивым любителям хмеля.','https://brewerydb-images.s3.amazonaws.com/beer/sSbZ3x/upload_g4K4Z4-medium.png',6.2,50,1.06,1.012,0,10,1);
