INSERT INTO "beers" VALUES (2151,'Critterless','American-Style Sour Ale','Critterless — американский кислый эль, сваренный с манго и вишней. Пиво имеет розоватый оттенок и наливается с небольшой белой пеной. Первоначальные вкусы эля, стягивающие рот терпкостью, переходят в приятную сладость, которая сопровождается ароматами спелых фруктов. Critterless заканчивается сухим с оттенком ржаной пряности.','https://brewerydb-images.s3.amazonaws.com/beer/WfgP3S/upload_y8TUp6-medium.png',8.6,NULL,NULL,NULL,0,21,1);
