INSERT INTO "beers" VALUES (2154,'Crooked Coast Altbier','German-Style Altbier','Вдохновленный оригинальным пивом Alt-style из Дюссельдорфа, Crooked Coast объединяет ароматы немецкого благородного хмеля и мюнхенского солода. Тонкий баланс солода и хмеля является определяющей чертой этого квинтэссенциального сессионного пива!','https://brewerydb-images.s3.amazonaws.com/beer/BeYS8Z/upload_Mi97bx-medium.png',5.1,25,1.044,1.008,0,19,1);
