INSERT INTO "beers" VALUES (2229,'Ctayt','American-Style Imperial Stout','','https://brewerydb-images.s3.amazonaws.com/beer/EWjtRY/upload_B1MCfK-medium.png',0,50,1.08,1.02,0,6,1);
