INSERT INTO "beers" VALUES (2644,'Darker Than Your Soul','American-Style Stout','Время раскрыть свою темную сторону. Darker Than Your Soul Stout — мягкий и сливочный стаут ​​с мощным вкусом и темным телом. Сварен из очищенного от шелухи солода для минимизации горечи и усиления вкуса.','https://brewerydb-images.s3.amazonaws.com/beer/D8c5Xv/upload_hPtNn9-medium.png',6.5,35,1.05,1.01,0,3,1);
