INSERT INTO "beers" VALUES (2735,'De Achste Hond Peach Sour','American-Style Sour Ale','Laughing Dogs Берут кислое фруктовое пиво. Слегка терпкое с огромным персиковым ароматом, ноткой дуба и послевкусием, напоминающим шампанское.','https://brewerydb-images.s3.amazonaws.com/beer/MDLWPq/upload_lkvJ80-medium.png',7,NULL,NULL,NULL,0,19,1);
