INSERT INTO "beers" VALUES (2747,'De Noel','American-Style Sour Ale','Темный кислый напиток, сваренный с добавлением праздничной специи — имбиря, мускатного ореха и чернослива.','https://brewerydb-images.s3.amazonaws.com/beer/pvN9ig/upload_8fX7mC-medium.png',6.5,NULL,NULL,NULL,0,10,1);
