INSERT INTO "beers" VALUES (2910,'Deep Dive Wet-Hopped Ale','Fresh "Wet" Hop Ale','Это пиво является витриной вкуса и аромата одного сорта хмеля: Centennial. «Влажный» в wet-hopped относится к хмелю, который был собран всего за несколько часов до добавления в пиво. Впечатляющее количество свежего цельного хмеля Centennial было добавлено в котел, что придало пиву очень специфическую, интенсивную горечь и аромат.','https://brewerydb-images.s3.amazonaws.com/beer/SoBlWq/upload_E0tgnx-medium.png',8.5,NULL,NULL,NULL,0,9,1);
