INSERT INTO "beers" VALUES (2912,'Deep Roots','American-Style Amber/Red Ale','Мы добавили «красный» в Red Cypress с освежающим гибридом немецкого и американского стилей! С ароматами хрустящего печеного хлеба, сбалансированной хмелевой горечью и чистым, сухим послевкусием, этот доступный эль доказывает, что наши корни уходят глубоко.','https://brewerydb-images.s3.amazonaws.com/beer/cJPPrw/upload_LHmxOi-medium.png',5.4,30,1.048,1.012,0,18,1);
