INSERT INTO "beers" VALUES (2916,'Deep Thought','English-Style Dark Mild Ale','Сварено с бесконечным величием и спокойствием. Насыщенное, насыщенное и темное. Это сильно охмеленное пиво взрывается цитрусовыми и ягодными вкусами... стоит задуматься?','https://brewerydb-images.s3.amazonaws.com/beer/y4uVIv/upload_wIOpJV-medium.png',4.7,10,1.03,1.004,0,20,1);
