INSERT INTO "beers" VALUES (2925,'deFallt','French-Style Bière de Garde','deFallt — это наша осенняя сезонная интерпретация пива бьер-де-гард «пива на хранение», которое традиционно варят ранней весной для употребления по завершении летнего сбора урожая. deFallt — мягкий и пряный напиток с нотками ванили из французского дуба, смешанный с корицей и медом полевых цветов с фермы Indiana Hunter’s Farm, что делает его вашим выбором deFallt на этот сезон.','https://brewerydb-images.s3.amazonaws.com/beer/o9Qt1h/upload_lW0WHb-medium.png',7,20,1.06,1.012,0,26,1);
INSERT INTO "beers" VALUES (2926,'DEFCON I BELGIAN QUAD','Belgian-Style Quadrupel','FACTION DEFCON I BELGIAN QUAD — ТРАДИЦИОННОЕ БЕЛЬГИЙСКОЕ ПИВО QUAD, СВАРЕННОЕ С ИСПОЛЬЗОВАНИЕМ CASTLE PILSNER И СОЛОДА CARAMUNICH, А ТАКЖЕ ХМЕЛЯ STYRIAN GOLDING','https://brewerydb-images.s3.amazonaws.com/beer/aec5Pl/upload_BqR0dg-medium.png',7.9,25,1.084,1.014,0,28,1);
INSERT INTO "beers" VALUES (2927,'DEFCON II BELGIAN TRIPEL','Belgian-Style Tripel','FACTION DEFCON II BELGIAN TRIBEL — ТРАДИЦИОННОЕ БЕЛЬГИЙСКОЕ ПИВО TRIBEL, СВАРЕННОЕ ИЗ БЕЛЬГИЙСКОГО 2-РЯДНОГО И НЕМЕЦКОГО MUNICHСКОГО СОЛОДА, А ТАКЖЕ ХМЕЛЯ SAAZ.','https://brewerydb-images.s3.amazonaws.com/beer/CyKtZC/upload_9sCEy0-medium.png',7.3,20,1.07,1.01,0,23,1);
INSERT INTO "beers" VALUES (2928,'DEFCON III BELGIAN DUBBEL','Belgian-Style Dubbel','FACTION DEFCON III BELGIAN DUBBEL — ТРАДИЦИОННОЕ БЕЛЬГИЙСКОЕ ПИВО DUBBEL, СВАРЕННОЕ ИЗ CRISP BROWN И WEYERMANN SPECIAL С МУЖСКИМ И SAAZ ХМЕЛЕМ','https://brewerydb-images.s3.amazonaws.com/beer/pI3hid/upload_UId91g-medium.png',6.9,20,1.06,1.012,0,11,1);
