INSERT INTO "beers" VALUES (2930,'DEFCON V BELGIAN TABLE BEER','Belgian-Style Table Beer','FACTION DEFCON V БЕЛЬГИЙСКОЕ СТОЛОВОЕ ПИВО — ЭТО ЛЕГКО ПЬЮЩЕЕ СТОЛОВОЕ ПИВО В БЕЛЬГИЙСКОМ СТИЛЕ, СВАРЕННОЕ С АНГЛИЙСКИМ ХМЕЛЕМ, БЕЛЬГИЙСКИМ СОЛОДОМ И ДРОЖЖАМИ.','https://brewerydb-images.s3.amazonaws.com/beer/3vVK1V/upload_OE56Yt-medium.png',4.8,5,1.008,1.004,0,30,1);
INSERT INTO "beers" VALUES (2931,'Defender IPA','American-Style India Pale Ale','Героически охмеленный золотистый IPA с сильными нотами тропических фруктов, мускулистой хмелевой горечью и невероятно сухим послевкусием. Brooklyn Defender IPA с гордостью является официальным пивом New York Comic Con.','https://brewerydb-images.s3.amazonaws.com/beer/46DjNi/upload_p5xBpp-medium.png',5.5,50,1.06,1.012,0,8,1);
INSERT INTO "beers" VALUES (2932,'Defenestration','Belgian-Style Blonde Ale','Мы узнали об этом слове, когда посетили Пражский Град. Дефенестрация буквально означает выбрасывать что-то или кого-то из окна. В замке есть комната, где это происходило с нежелательными политиками! Не такая уж плохая идея. Это пиво не относится к какому-то определенному стилю, просто к тому, над чем Винни работал некоторое время. Оно ОЧЕНЬ сухое, горькое, хмельное и полнотелое.','https://brewerydb-images.s3.amazonaws.com/beer/1j67Xx/upload_zAghq6-medium.png',7.25,15,1.054,1.008,0,24,1);
