INSERT INTO "beers" VALUES (2977,'Demons Surround Me (Ghost 768)','British-Style Imperial Stout','САМОЕ БОЛЬШОЕ пиво, которое мы когда-либо делали! Этот насыщенный + сочный стаут ​​источает интенсивный вкус, но также и тонкий характер. Предназначен для потягивания в созерцательный вечер.','https://brewerydb-images.s3.amazonaws.com/beer/ve4QXT/upload_LfE2d3-medium.png',19,45,1.08,1.02,0,20,1);
