INSERT INTO "beers" VALUES (3112,'Diät Pils','German-Style Pilsener','Низкокалорийный, светлый лагер в немецком стиле. Слегка хмельной, шипучий и абсолютно сухой, без остаточного сахара (похоже на игристое вино).','https://brewerydb-images.s3.amazonaws.com/beer/s8zSvJ/upload_HPovRa-medium.png',3.7,25,1.044,1.006,0,24,1);
