INSERT INTO "beers" VALUES (3132,'Dig','American-Style Pale Ale','Достаньте открывалку для бутылок, потому что этот Pale Ale — то, что вам нужно. Хмель Sorachi Ace придает пиву свежий весенний привкус с невероятным ароматом лимона. Nelson Sauvin — следующий в ряду с взрывами маракуйи, манго и персика. Американские фавориты Cascade и Centennial завершают этот свежий, чистый Pale Ale. Угощайтесь!','https://brewerydb-images.s3.amazonaws.com/beer/Ji4pSd/upload_qT11kA-medium.png',5.6,30,1.044,1.008,0,15,1);
