INSERT INTO "beers" VALUES (3136,'Diminished','Wild Beer','Этот кислый эль был подвергнут бретт-брожению, а затем выдерживался в течение года с добавлением смеси различных лактокультур и, наконец, смешан с терпкой вишней. Sublime','https://brewerydb-images.s3.amazonaws.com/beer/hftyci/upload_ZVSUu6-medium.png',5,NULL,NULL,NULL,0,25,1);
