INSERT INTO "beers" VALUES (3790,'Drink Trees','American-Style Pale Ale','Отпразднуйте День древонасаждения 2018 года с этим особым светлым элем, сваренным с грейпфрутом и можжевельником и ферментированным на кедровых щепах.','https://brewerydb-images.s3.amazonaws.com/beer/1lcsPU/upload_kqhY8y-medium.png',4.7,30,1.044,1.008,0,8,1);
INSERT INTO "beers" VALUES (3791,'Drink''in the Sun','Light American Wheat Ale or Lager without Yeast','','https://brewerydb-images.s3.amazonaws.com/beer/oVt8JN/upload_E3YVQx-medium.png',2.8,10,1.036,1.004,0,16,1);
INSERT INTO "beers" VALUES (3792,'Drink''in the Sunbelt','Light American Wheat Ale or Lager with Yeast','«Drink’in» — очень хмельной, ароматный сессионный эль, сваренный с равными долями солодового ячменя и солодовой пшеницы, и сильно охмеленный в сухом виде цитрусовым американским хмелем Amarillo. Название и вдохновение взяты из «Drink’in the Sun», пива, которое Mikkeller ранее варил в Европе, используя рецепт, удивительно похожий на тот, который Jester King уже использовал для многочисленных тестовых партий, которые должны были послужить основой для будущего сезонного выпуска.','https://brewerydb-images.s3.amazonaws.com/beer/q2cWOY/upload_7CHj9r-medium.png',3.9,10,1.036,1.006,0,7,1);
