INSERT INTO "beers" VALUES (3843,'Dry Pear Cider','Traditional Perry','Этот легкий, свежий сидр обладает терпким ароматом и отчетливым грушевым вкусом, завершаясь ярким ощущением во рту, щекочущим язык.','https://brewerydb-images.s3.amazonaws.com/beer/jC0TAa/upload_ajKV41-medium.png',4,NULL,1.05,1,0,16,1);
INSERT INTO "beers" VALUES (3844,'Dry Raspberry Cider','Traditional Perry','Этот сидр с аппетитным малиновым вкусом имеет легкое тело и игристое послевкусие.','https://brewerydb-images.s3.amazonaws.com/beer/Suw3lO/upload_7z7rT7-medium.png',4,NULL,1.05,1,0,25,1);
