INSERT INTO "beers" VALUES (3849,'Dry Wit','Belgian-Style White (or Wit) / Belgian-Style Wheat','Золотая пряжа с прекрасной пеной. Сухой с бодрящим медовым ароматом, подается с долькой апельсина. 
INSERT INTO "beers" VALUES (3850,'Dry-Hopped Censored Ale','Specialty Beer','Солодовый, поджаренный и обманчиво мягкий… Этот янтарный эль приобретает более поджаренный привкус какао благодаря использованию шоколадного солода.','https://brewerydb-images.s3.amazonaws.com/beer/dFaMLe/upload_XOSrHC-medium.png',6.7,NULL,1.03,1.006,0,6,1);
INSERT INTO "beers" VALUES (3851,'Dry-Hopped Lowerline','American-Style Sour Ale','Кислый эль в американском стиле, охмелённый хмелем Galaxy.','https://brewerydb-images.s3.amazonaws.com/beer/UKOnU5/upload_eL4VYc-medium.png',5,NULL,NULL,NULL,0,18,1);
INSERT INTO "beers" VALUES (3852,'Dry-Hopped on the Seas','American-Style India Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/7PPkHo/upload_nFIsAp-medium.png',0,50,1.06,1.012,0,19,1);
INSERT INTO "beers" VALUES (3853,'Dry-Hopped Pilsner','German-Style Pilsener','Немецкий хмель Saphir сочетает в себе динамизм Нового Света и благородство Старого Света в этом суперароматном пильзнере, охмеленном в сухом виде.','https://brewerydb-images.s3.amazonaws.com/beer/byr1uU/upload_ssIq3H-medium.png',5.5,25,1.044,1.006,0,24,1);
