INSERT INTO "beers" VALUES (3976,'Dundalk Calling','Imperial or Double India Pale Ale','Наслаждайтесь этим двойным IPA с тройным сухим охмелением и добавлением цитрусовых.','https://brewerydb-images.s3.amazonaws.com/beer/yDfCH4/upload_fSP6Bd-medium.png',8,65,1.075,1.012,0,9,1);
INSERT INTO "beers" VALUES (3977,'Dundee Export Scotch Ale','Strong Ale','Наш Dundee Scotch Ale начинается с традиционной сладости и заканчивается полным солодовым вкусом. Не обманывайтесь темным цветом — это пиво восхитительно и на удивление легко пьется.','https://brewerydb-images.s3.amazonaws.com/beer/fyZtJ0/upload_ZErx1u-medium.png',6.5,30,1.06,1.014,0,6,1);
