INSERT INTO "beers" VALUES (4087,'East Brown And Down','American-Style Brown Ale','Насыщенный и поджаренный коричневый эль, сваренный с добавлением медового солода и овсяных хлопьев для получения насыщенного солодового пива, сбалансированного тонким английским хмелем.','https://brewerydb-images.s3.amazonaws.com/beer/3dpeaJ/upload_sJXSBD-medium.png',6.5,25,1.04,1.01,0,5,1);
