INSERT INTO "beers" VALUES (4146,'Eclipse - Rittenhouse Rye','Wood- and Barrel-Aged Strong Beer','Выдержанный в бочках Империал Стаут с нотками темного шоколада, эспрессо, дуба, ванили и табачными оттенками. Сложный и вкусный.','https://brewerydb-images.s3.amazonaws.com/beer/P6k4Ru/upload_t0zEmU-medium.png',11.9,NULL,NULL,NULL,0,25,1);
INSERT INTO "beers" VALUES (4147,'Eclipse Elijah Craig 18 year','American-Style Imperial Stout','Это трехлитровая бутылка из одной бочки Eclipse, выдержанной в бочках Elijiah Craig 18 лет. Эта бочка была известна как "Epic Barrel". Было изготовлено всего три трехлитровые бутылки.','https://brewerydb-images.s3.amazonaws.com/beer/YROQDO/upload_TsszXK-medium.png',9.5,50,1.08,1.02,0,19,1);
