INSERT INTO "beers" VALUES (4266,'Eleanor','Golden or Blonde Ale','Eleanor — золотистый фермерский эль, ферментированный в винных бочках с использованием смеси Brettanomyces и бактериальных культур, некоторые из которых были выращены нами из наших любимых коммерческих сортов кислого пива.','https://brewerydb-images.s3.amazonaws.com/beer/frN60M/upload_ecywIA-medium.png',6.8,15,1.045,1.008,0,21,1);
