INSERT INTO "beers" VALUES (4587,'ESB - Endless Summer Beer','Extra Special Bitter','','https://brewerydb-images.s3.amazonaws.com/beer/VOX8vQ/upload_NgdQgX-medium.png',0,30,1.046,1.01,0,2,1);
INSERT INTO "beers" VALUES (4588,'ESB - Nitrogenated','Extra Special Bitter','','https://brewerydb-images.s3.amazonaws.com/beer/WFy4vs/upload_jTLejK-medium.png',5,30,1.046,1.01,0,14,1);
INSERT INTO "beers" VALUES (4589,'ESB - Tasting Room','Extra Special Bitter','','https://brewerydb-images.s3.amazonaws.com/beer/IURBOA/upload_NR8zxW-medium.png',0,30,1.046,1.01,0,16,1);
INSERT INTO "beers" VALUES (4590,'ESB Ale','Special Bitter or Best Bitter','Этот ESB — классический британский биттер extra special, приготовленный свежим с американским уклоном. Красивый медный цвет с янтарной шапкой, этот классический стиль содержит пять различных солодов, включая импортные английские солода, и три сорта хмеля. Богатое солодовое начало с карамельными нотками, которые развиваются в мягкое, приятное хмелевое послевкусие.','https://brewerydb-images.s3.amazonaws.com/beer/B4XeLe/upload_IuN24p-medium.png',5.5,28,1.038,1.006,0,18,1);
INSERT INTO "beers" VALUES (4591,'ESB Amber','Extra Special Bitter','Экстра-специальный биттер в британском стиле, вдохновленный классическими британскими паб-элями с ровным балансом девяти различных солодов, идеально сочетающихся с ноткой мягкого хмеля. ESB/Amber имеет прекрасное глубокое янтарное тело и является мягким, легко пьющимся элем.','https://brewerydb-images.s3.amazonaws.com/beer/WJn5Gw/upload_OaBeRT-medium.png',5.5,30,1.046,1.01,0,5,1);
INSERT INTO "beers" VALUES (4592,'Escanaba Black Beer','Dark American-Belgo-Style Ale','Escanaba Black Beer — солодовый, темный и вкусный. Его умеренная прожаристость сбалансирована легкой хмелевой горечью. Результат — шелковисто-гладкое послевкусие этого сессионного эля, что делает его идеальным пивом для всего, что может предложить север.','https://brewerydb-images.s3.amazonaws.com/beer/XiaM56/upload_A53ZTi-medium.png',4.5,NULL,NULL,NULL,0,15,1);
INSERT INTO "beers" VALUES (4593,'ESCape','California Common Beer','','https://brewerydb-images.s3.amazonaws.com/beer/EUq27K/upload_PN5PbN-medium.png',0,35,1.045,1.01,0,21,1);
INSERT INTO "beers" VALUES (4594,'Escape Artist','American-Style Pale Ale','Мы верим, что пиво — это магия! Escape Artist Extra Special Pale заставит вас поверить в него. Легкое, среднетелое пиво, мы используем два классических американских сорта хмеля. Centennial и Cascade создают смесь таинственности и сложности. Здесь нет дыма и зеркал.','https://brewerydb-images.s3.amazonaws.com/beer/2xdU40/upload_40M1hQ-medium.png',5.5,30,1.044,1.008,0,8,1);
INSERT INTO "beers" VALUES (4595,'Escape Route','German-Style Kölsch / Köln-Style Kölsch','','https://brewerydb-images.s3.amazonaws.com/beer/UpENQG/upload_oLSedH-medium.png',5,18,1.042,1.006,0,11,1);
INSERT INTO "beers" VALUES (4596,'Escape to Colorado IPA','American-Style India Pale Ale','Этот цветочный американский IPA предлагает сложный аромат легких цитрусовых, варьирующийся от мандарина до тропических фруктов. Ультра-премиальный солод Maris Otter обеспечивает легкую ореховую сладость, которая уравновешивает сильный хмелевой профиль и оставляет только легкую горечь в послевкусии.','https://brewerydb-images.s3.amazonaws.com/beer/XMfkby/upload_bDqN96-medium.png',6.2,50,1.06,1.012,0,6,1);
INSERT INTO "beers" VALUES (4597,'Escaped Goat','South German-Style Hefeweizen / Hefeweissbier','','https://brewerydb-images.s3.amazonaws.com/beer/ULsj7R/upload_eYfb9p-medium.png',5.6,10,1.047,1.008,0,11,1);
INSERT INTO "beers" VALUES (4598,'Escapist','American-Style India Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/kpL9g1/upload_b8vTg0-medium.png',6.7,50,1.06,1.012,0,20,1);
INSERT INTO "beers" VALUES (4599,'Eschaton','Other Belgian-Style Ales','как насчет крепкого, солодового пива, которое напоминает хорошее красное вино и с более сухим послевкусием, чем можно было бы ожидать при 10,5% ABV? 
INSERT INTO "beers" VALUES (4600,'Escoffier','American-Style Sour Ale','Escoffier — это наше широко разрекламированное сотрудничество с New Belgium Brewing в Форт-Коллинзе, штат Колорадо. Совпав с их приходом в Мичиган в августе 2012 года, этот проект начался с дружеской встречи в нашем пабе. После нескольких кружек пива и еды идея сварить пиво вместе начала обретать форму. Сюрприз! 
INSERT INTO "beers" VALUES (4601,'Esmé - Barrel Aged','Wood- and Barrel-Aged Beer','Эта версия нашего фермерского эля двойной крепости выдерживалась в винных бочках в течение 4 месяцев. Было сделано два варианта, в ограниченном количестве, и их можно найти, пока они не исчезли в баре и на различных специальных мероприятиях.','https://brewerydb-images.s3.amazonaws.com/beer/pAsC6b/upload_6QEk0M-medium.png',8,NULL,NULL,NULL,0,13,1);
INSERT INTO "beers" VALUES (4602,'Esperanza','Wood- and Barrel-Aged Beer','Barrel Aged Wee Heavy выдержан в бочках из-под рома Siesta Key Spiced. Наш первый бутылочный релиз.','https://brewerydb-images.s3.amazonaws.com/beer/QItmt1/upload_g0obYz-medium.png',8,NULL,NULL,NULL,0,4,1);
INSERT INTO "beers" VALUES (4603,'Espress-O-Face','Coffee-Flavored Beer','Овсяный стаут, сваренный с добавлением зерен эспрессо.','https://brewerydb-images.s3.amazonaws.com/beer/ECCnAi/upload_QFOzov-medium.png',0,15,1.03,1.006,0,10,1);
INSERT INTO "beers" VALUES (4604,'Espresso Amber Ale','Specialty Beer','Крепкий янтарный эль, сваренный с использованием местного обжаренного органического эспрессо, соответствующего принципам справедливой торговли, с помощью наших друзей из Coffee by Design. Этот солодовый янтарный эль сварен с большим количеством мюнхенского солода, что обеспечивает идеальную основу для насыщенного, жареного вкуса, придаваемого эспрессо. 
INSERT INTO "beers" VALUES (4605,'Espresso Cone Stout','American-Style Stout','','https://brewerydb-images.s3.amazonaws.com/beer/Q1WE2i/upload_S8EDge-medium.png',6,35,1.05,1.01,0,18,1);
INSERT INTO "beers" VALUES (4606,'Espresso Imperial Russian Stout','American-Style Imperial Stout','Stone Imperial Russian Stout настолько густой, насыщенный и, в общем, греховный, что вы можете беспокоиться о том, что будете обречены на огненные ямы только за то, что подумаете о глотке. Он почти не кажется злым. Однако будьте уверены, что, хотя этот, казалось бы, пагубный напиток действительно черный, как грех, мы гарантируем, что при его производстве не было совершено никакого реального греха... вам придется добавить это самостоятельно. Это дополнение Odd Year было сварено в аутентичном, историческом стиле императорского русского стаута, но с добавлением нескольких сотен фунтов зерен эспрессо от наших друзей из Ryan Bros. Coffee. На самом деле, это можно считать исторически точным, поскольку русская знать любила как земляные напитки для завтрака, так и крепкое темное пиво. Сама Екатерина Великая подкрепляла свою утонченность в управлении империей, выпивая пять чашек черного кофе каждое утро во время своего долгого правления. Хотя мы не смогли вместить пять чашек кофе в каждую бутылку этого темного восхитительного напитка, мы заверяем вас, что глубокие, насыщенные вкусы эспрессо безошибочны и прекрасно сочетаются с жареной горечью темного солода. Кэтрин бы полностью одобрила.','https://brewerydb-images.s3.amazonaws.com/beer/lmHzVK/upload_7OV1Jb-medium.png',10.5,50,1.08,1.02,0,15,1);
INSERT INTO "beers" VALUES (4607,'Espresso Love Breakfast Stout','Oatmeal Stout','Овсянка и 20 фунтов свежеобжаренного кофе из Ugly Mug Café способствуют слегка меловому характеру эспрессо, сбалансированному богатой, сладкой сливочной солодовостью. Кофейное послевкусие растворяется в шоколаде с легкой жареной горчинкой. Сварено из 100% кофе, произведенного по принципам справедливой торговли из Ugly Mug Café and Roastery в Ипсиланти, штат Мичиган.','https://brewerydb-images.s3.amazonaws.com/beer/WqSfTV/upload_1FXEty-medium.png',7.7,20,1.038,1.008,0,7,1);
INSERT INTO "beers" VALUES (4608,'Espresso Oak Aged Yeti','American-Style Imperial Stout','Щедрая добавка денверского эспрессо Pablo’s добавляет еще один уровень сложности этому пиву, сочетая характер ванильного дуба, интенсивную жареную солодовость и яркий хмелевой профиль, чтобы создать совершенно новую породу мифического существа. Это официально: теперь вы можете съесть Йети на завтрак.','https://brewerydb-images.s3.amazonaws.com/beer/aywDqA/upload_rIc8JE-medium.png',9.5,50,1.08,1.02,0,23,1);
INSERT INTO "beers" VALUES (4609,'Espresso Porter','Robust Porter','Это пиво — совместный проект Palmetto Brewing и/или соседей, Charleston Coffee roasters. После многих лет совместной работы под одной крышей Эд и Лоуэлл расшифровали, чтобы объединить свои коллективные интеллектуальные силы вокруг концепции использования профессионально и локально обжаренного кофе в этом пиве. После нескольких обжарок и различных кофейных зерен окончательный рецепт — это настоящее произведение искусства, сочетающее смелые вкусы в тонком балансе. Это пиво вкусное и очень освежающее... подходит для любого сезона в Лоукантри.','https://brewerydb-images.s3.amazonaws.com/beer/DPnyEP/upload_fjmAc8-medium.png',5.3,25,1.045,1.008,0,29,1);
INSERT INTO "beers" VALUES (4610,'Espresso Stout','American-Style Imperial Stout','Этот стаут ​​начинается со смеси темных солодов. Затем он смешивается со свежеобжаренным и сваренным эспрессо, любезно предоставленным нашим другом Джоном Анастасом. Результатом является бесплатная смесь жареных солодов и кофейных вкусов с мягким послевкусием. Он обязательно удовлетворит вашего внутреннего кофемана.','https://brewerydb-images.s3.amazonaws.com/beer/saXgKy/upload_SiqmPf-medium.png',10,50,1.08,1.02,0,5,1);
INSERT INTO "beers" VALUES (4611,'Espressway','Oatmeal Stout','Овсяный стаут ​​с кофе холодного отжима','https://brewerydb-images.s3.amazonaws.com/beer/ObvmCL/upload_z0zUGC-medium.png',6.5,20,1.038,1.008,0,24,1);
INSERT INTO "beers" VALUES (4612,'Esprit De Corps Ale','Specialty Beer','','https://brewerydb-images.s3.amazonaws.com/beer/sgGY0G/upload_9A5Ioi-medium.png',0,NULL,1.03,1.006,0,11,1);
INSERT INTO "beers" VALUES (4613,'Esquisse','American-Style Sour Ale','','https://brewerydb-images.s3.amazonaws.com/beer/lTekYi/upload_6HkeiL-medium.png',8,NULL,NULL,NULL,0,21,1);
INSERT INTO "beers" VALUES (4614,'Essential Pale Ale','American-Style Pale Ale','Essential Pale Ale имеет глубокий золотистый цвет с яркими ароматами летних косточковых фруктов, которые вырываются из бокала. Первый глоток приносит крепкую горечь, сопровождаемую тонкой сладостью и достигающую кульминации в освежающе сухом послевкусии. Что выделяет Essential среди его собратьев по Pale Ale, так это дразнящие персиковые и абрикосовые вкусы, которые исходят от смеси американского хмеля, который мы представляем в качестве сухого хмеля с использованием нашего запатентованного HOPZOOKA®.','https://brewerydb-images.s3.amazonaws.com/beer/MwSypd/upload_y937GO-medium.png',5.5,30,1.044,1.008,0,3,1);
INSERT INTO "beers" VALUES (4615,'Esser''s Anniversary Ale','Specialty Beer','','https://brewerydb-images.s3.amazonaws.com/beer/Urc1VC/upload_1PjPQP-medium.png',0,NULL,1.03,1.006,0,27,1);
INSERT INTO "beers" VALUES (4616,'Esser''s Best Original','American-Style Lager','Среднетелое пиво в старонемецком стиле, сваренное с добавлением различных сортов карамельного солода, что обеспечивает глубокий янтарный цвет, полный вкус и исключительное послевкусие.','https://brewerydb-images.s3.amazonaws.com/beer/qipybT/upload_3FnbAv-medium.png',0,5,1.04,1.006,0,19,1);
INSERT INTO "beers" VALUES (4617,'Esser''s Cross Plains Special','German-Style Pilsener','Освежающий, золотистый, среднетелый пилснер с приятным солодовым вкусом и легким охмелением для хорошего баланса.','https://brewerydb-images.s3.amazonaws.com/beer/T2Ymku/upload_h4yvbZ-medium.png',0,25,1.044,1.006,0,10,1);
INSERT INTO "beers" VALUES (4618,'ESSEX BEAST','Coffee-Flavored Beer','Фольклор повествует о многих мифических существах во всех уголках земного шара, от йети в Гималаях и снежного человека в лесах Северной Америки до чудовища озера Лох-Несс и зверя из Бодмин-Мур немного ближе к дому, например. Юго-Восток Англии действительно имеет своего легендарного монстра, могущественного зверя Эссекса! Гигантский портер, изготовленный из прекрасной смеси светлого, шоколадного и темного кристаллического солода. Сочные ароматы собираются вместе, чтобы дать прекрасный нос ириски и кофе, вкус полный и сложный с пряным хмелевым оттенком, за которым следует больше вкусов патоки и мокко. Если вы столкнетесь с ним, не бойтесь противостоять, но, пожалуйста, обращайтесь с ним осторожно!','https://brewerydb-images.s3.amazonaws.com/beer/vK6Fln/upload_7J6ck4-medium.png',6.2,15,1.03,1.006,0,21,1);
INSERT INTO "beers" VALUES (4619,'ESSEX BORDER','Belgian-Style Blonde Ale','Актуальный вопрос большого культурного значения — кто ваша самая любимая блондинка из Эссекса? Джоан Симс, Дениз Ван Аутен, Джоди Марш, Ноэль Эдмондс или даже Даррен Дэй, может быть? Ну, наш — определенно Essex Border. Чудесное, легко пьющееся золотистое пиво с фруктовыми, пряными нотками, уступающими место сладким, мягким апельсиновым ноткам и гармоничным послевкусием. Как не любить вкусную, гармоничную блондинку из Эссекса?','https://brewerydb-images.s3.amazonaws.com/beer/4MvpJU/upload_OQ13f0-medium.png',4.8,15,1.054,1.008,0,4,1);
INSERT INTO "beers" VALUES (4620,'Estaminet','German-Style Pilsener','Премиум Пилс','https://brewerydb-images.s3.amazonaws.com/beer/zahbZI/upload_JlzAA7-medium.png',5.2,25,1.044,1.006,0,7,1);
INSERT INTO "beers" VALUES (4621,'Estate Ale','Specialty Beer','Здесь, на залитых солнцем полях Северной долины Калифорнии, черная почва полна надежд. 
INSERT INTO "beers" VALUES (4622,'Esther Belgian Christmas Ale','Other Belgian-Style Ales','','https://brewerydb-images.s3.amazonaws.com/beer/8JtnHj/upload_0ZsZZa-medium.png',9.5,NULL,NULL,NULL,0,2,1);
INSERT INTO "beers" VALUES (4623,'Esther''s Li''l Secret','Specialty Stouts','У Эстер Прайс была мечта. Открыть магазин. И поделиться с миром своими приготовленными вручную, сделанными с любовью сладостями. Так в течение 30 лет, к блаженству дегустаторов, также известных как семья и друзья, она совершенствовала вечный выбор восхитительных сладостей. В 1952 году двери ее первого магазина в Дейтоне, штат Огайо, распахнулись. И мир ворвался внутрь. Шоколад Эстер быстро стал и остается самым любимым на Среднем Западе. Ее секрет? Некоторые вещи лучше выпить, чем сказать. Маленький секрет Эстер. Декадентски вкусный шоколадный вишневый кордиал стаут. О чем вы мечтаете?','https://brewerydb-images.s3.amazonaws.com/beer/1yUgJa/upload_nD42Rc-medium.png',8,35,1.08,1.02,0,8,1);
INSERT INTO "beers" VALUES (4624,'Esther''s Li''l Secret','American-Style Pale Ale','Зимний белый эль Esther''s Li''l Secret 2017 года с шоколадом и ванильным кремом','https://brewerydb-images.s3.amazonaws.com/beer/L30WYV/upload_ThZj1R-medium.png',8,30,1.044,1.008,0,1,1);
INSERT INTO "beers" VALUES (4625,'Esther''s Lil''l Secret','Scotch Ale','Помните магию, которая случилась, когда шоколад столкнулся с арахисовым маслом? Или когда печенье выскользнуло из руки в ничего не подозревающий стакан молока? А теперь представьте себе другой эйфорический сценарий. 
INSERT INTO "beers" VALUES (4626,'Estimated Profit','American-Style Imperial Stout','Estimated Profit — новейшее предложение от PBC. Это имперский стаут ​​с высоким содержанием алкоголя и обилием вкуса! В зерновой засыпи было использовано щедрое количество шоколада и жареного солода, чтобы придать ему классические шоколадные, карамельные и кофейные нотки, которые должен иметь настоящий стаут. Он также сохраняет прекрасное шелковистое ощущение во рту, классическое для этого стиля. Эта красота идеально подходит для холодной зимы в Колорадо. Название Estimated Profit отдает дань уважения классическому треку Grateful Dead, который является любимым треком съемочной группы. Этот стаут ​​выпускается очень ограниченным тиражом. Мы консервировали всего около 1150 банок, и когда они закончатся, то закончатся. Наслаждайтесь Предсказателем судьбы!','https://brewerydb-images.s3.amazonaws.com/beer/N83owo/upload_eE3eAJ-medium.png',11.9,50,1.08,1.02,0,10,1);
INSERT INTO "beers" VALUES (4627,'Estío','American-Style Pale Ale','Pale Ale - American
INSERT INTO "beers" VALUES (4628,'Estival Cream Stout','Sweet or Cream Stout','Сваренный с апельсиновым медом и выдержанный на сладкой апельсиновой цедре, этот летний стаут ​​такой же легкий, как само лето. Это взрослая версия одного из ваших любимых детских воспоминаний. Он имеет среднюю плотность, подчеркнутую нотками кофе и темного шоколада. Именно тонкий цитрусовый вкус апельсиновой цедры, сбалансированный со сладостью меда, делает этот напиток освежающим и удовлетворяющим летним лакомством.','https://brewerydb-images.s3.amazonaws.com/beer/LUUsuK/upload_vRn0Nu-medium.png',5.8,15,1.045,1.012,0,17,1);
INSERT INTO "beers" VALUES (4629,'Estival Dichotomous','French & Belgian-Style Saison','Estival Dichotomous 2014 года, наш летний сэзон, был сварен с ромашкой и повторно обработан клубникой. Мы остались верны мудрости нашего друга Ивана де Бейтса из Brasserie de la Senne, который пишет: «Если используются специи, то с максимальной умеренностью. Сэзон ни в коем случае не является супом из специй». Дикие дрожжи и бактерии естественного происхождения придают этому нефильтрованному, непастеризованному и на 100% выдержанному в бутылке пиву ощущение места.','https://brewerydb-images.s3.amazonaws.com/beer/RPbSZ7/upload_z1Gd0R-medium.png',6,20,1.055,1.004,0,9,1);
INSERT INTO "beers" VALUES (4630,'Estrada De Terra','American-Style Brown Ale','Это пиво, созданное в сотрудничестве с Канзас-Сити, сочетает в себе Parisi Artisan Coffee с нашим среднетелым коричневым элем, в результате чего получается идеальный холодный напиток с кофеином. Как и в свежезаваренном кофейнике, аромат мгновенный, раскрывающийся ореховым, слегка поджаренным вкусом эспрессо, который заканчивается мягко. Наслаждайтесь Estrada de Terra так же, как вы бы наслаждались любой отличной чашкой кофе или крафтовым пивом — в хорошей компании.','https://brewerydb-images.s3.amazonaws.com/beer/WuQmbA/upload_5aQRWH-medium.png',5.7,25,1.04,1.01,0,11,1);
INSERT INTO "beers" VALUES (4631,'Estrella Damm','American-Style Lager','Сварено по оригинальному рецепту 1876 года. Золотистый с янтарными тонами и несколькими зелеными бликами. Чистый и яркий. Пена имеет цвет экрю и долго держится. Мы рекомендуем пить это пиво в бокале, слегка закрытом сверху.','https://brewerydb-images.s3.amazonaws.com/beer/wYDgay/upload_GSb8ux-medium.png',5.4,5,1.04,1.006,0,19,1);
INSERT INTO "beers" VALUES (4632,'Estrella Damm (UK)','American-Style Lager','Любить свою работу и обращать внимание на каждую деталь — вот формула Estrella Damm. Сварено по оригинальному рецепту 1876 года. У нас нет секретов. Просто хорошо сделанная работа. 
INSERT INTO "beers" VALUES (4633,'Estrella Damm Inedit','Belgian-Style White (or Wit) / Belgian-Style Wheat','Inedit был создан в 2008 году Ферраном Адриа и его командой сомелье из elBulli, которые верили в необходимость пива, которое сопровождало бы и дополняло разнообразные вкусы, встречающиеся в современной гастрономии. 
INSERT INTO "beers" VALUES (4634,'Et Tu Brut','Wood- and Barrel-Aged Beer','','https://brewerydb-images.s3.amazonaws.com/beer/d0d0xV/upload_Qw2j9f-medium.png',0,NULL,NULL,NULL,0,10,1);
INSERT INTO "beers" VALUES (4635,'Et Tu Juiceous','Juicy or Hazy Imperial or Double India Pale Ale','Наша вариация Hazy DIPA наполнена овсом и сочетает кремовый вкус с взрывом тропических фруктов и приятной сыростью, чтобы все это сбалансировать. Сухое охмеление хмелями Cashmere и Eureka.','https://brewerydb-images.s3.amazonaws.com/beer/MDGNfO/upload_AKhKf2-medium.png',8,65,1.07,1.012,0,23,1);
INSERT INTO "beers" VALUES (4636,'Été','French & Belgian-Style Saison','Второе пиво в нашей линейке сезонных сэзонов. Été варится с использованием местной бузины в качестве основы рецепта. Солод спельты используется для подчеркивания легких медовых и ореховых нот бузины, а немецкий хмель Hallertau Blanc и Huell Melon используется для усиления цветочных и цитрусовых нот, объединяясь в симфонию летних вкусов. Мы собрали бузину в полумиле от пивоварни с помощью местного любителя живых изгородей Urbane Forager.','https://brewerydb-images.s3.amazonaws.com/beer/yMWJgH/upload_XtSWVK-medium.png',5.6,20,1.055,1.004,0,25,1);
INSERT INTO "beers" VALUES (4637,'Eternal','Session India Pale Ale','Удар сильно охмеленного IPA с сессионностью классического британского светлого эля. В этом светлом пиве доминируют хмели Simcoe и Centennial, привнося в него сильный аромат мандарина и долгий, утоляющий цитрусовый привкус, который продолжается и продолжается.','https://brewerydb-images.s3.amazonaws.com/beer/HA5oSN/upload_SgwecY-medium.png',4.1,NULL,NULL,NULL,0,8,1);
INSERT INTO "beers" VALUES (4638,'Eternal Darkness','English-Style India Pale Ale','Это был лишь вопрос времени, пока наш Eternal session IPA не нашел вдохновения в нашем Dark Arches black IPA, чтобы поэкспериментировать с темной стороной хмеля. И вуаля, узрите наш фирменный черный session IPA крепостью 4,1% ABV. Ожидайте ту же питкость и хмелевую атаку Eternal, но с новым измерением жареного солода, который придает свой собственный почерк вкусовому профилю. Но не позволяйте названию обмануть вас. Eternal Darkness так же созрел для вашей хмельной дозы на открытом солнце, как и его более легкий близнец.','https://brewerydb-images.s3.amazonaws.com/beer/cYt7O1/upload_uFZ3zq-medium.png',4.1,35,1.05,1.012,0,15,1);
INSERT INTO "beers" VALUES (4639,'Eternal Flame','German-Style Doppelbock','','https://brewerydb-images.s3.amazonaws.com/beer/3lHz6c/upload_osT3Gh-medium.png',9,17,1.074,1.014,0,22,1);
INSERT INTO "beers" VALUES (4640,'Eternal Now','Wood- and Barrel-Aged Sour Beer','Eternal Now вызывает спектральные слои фанка и кислоты. Это выдержанное в бочках кислое пиво настояно на пикантном Limu Omani (персидский черный лайм), удваивая пахучую сложность базового пива. Это лаймы, которые были выварены в соленой воде, а затем оставлены для сушки и самосбраживания, пока они не станут высушенной, выдержанной специей. Невероятно насыщенные и мускусные ароматы этого преображенного цитрусового идеально подходят для выдержанного хмеля и нашей сверхъестественной домашней смешанной культуры. Невероятно многогранный и удовлетворяющий.','https://brewerydb-images.s3.amazonaws.com/beer/RKIWah/upload_lbYMc8-medium.png',6,NULL,NULL,NULL,0,3,1);
INSERT INTO "beers" VALUES (4641,'Eternal Rest','American-Style Imperial Stout','Мы тщательно отобрали бочки со стаутом из нашего погреба и смешали их, чтобы запечатлеть уникальный момент времени. Eternal Rest — это сочетание русского имперского стаута, выдержанного 19 месяцев в бочках из-под арманьяка, и имперского стаута, выдержанного 25 месяцев в бочках из-под бурбона. Затем купаж выдерживался на свежих стручках ванили и поджаренном кокосе. Приветствуем новые начинания и бесконечные горизонты.','https://brewerydb-images.s3.amazonaws.com/beer/jFFGJs/upload_q8tpVd-medium.png',14,50,1.08,1.02,0,26,1);
INSERT INTO "beers" VALUES (4642,'Eternal Youth Bière d’Abricot','French & Belgian-Style Saison','В поисках целебных вод и золота испанский исследователь XVI века Хуан Понсе де Леон отправился в то, что сейчас является Флоридой. Многие последовали по его стопам, поскольку человечество постоянно ищет магические зелья для восстановления своей молодости. Употребление свежего местного пива, такого как Eternal Youth Bière d’Abricot, не сделает время вечным, но может помочь более эффективно размышлять о хороших моментах, пережитых на протяжении всей жизни. И в этих инкапсулированных воспоминаниях можно жить в определенной утопии Вечной Молодости. Этот французский фермерский стиль включает дрожжи Bière de Grade, а также абрикосы, которые, как предполагается, способствуют здоровью и долголетию. Затем этот эликсир жизни выдерживается в дубовых бочках, чтобы придать сложность этому сеансному наслаждению позднего лета. Искупайтесь в этом фонтане молодости и откройте для себя свою Вечную Молодость.','https://brewerydb-images.s3.amazonaws.com/beer/CD6byk/upload_6IX2jW-medium.png',5.2,20,1.055,1.004,0,18,1);
INSERT INTO "beers" VALUES (4643,'Ethos','American-Style India Pale Ale','Ethos — это не обычный IPA. Изготовленное из палитры из шести сортов хмеля, а затем дважды охмеленное насухо тремя различными видами хмеля, это пиво имеет насыщенный, яркий и красивый аромат и вкус, который взрывается сочными хмелевыми нотами с нотами сосны, смолы, грейпфрута и карамели.','https://brewerydb-images.s3.amazonaws.com/beer/8ffHyG/upload_TwfZJS-medium.png',6.8,50,1.06,1.012,0,19,1);
INSERT INTO "beers" VALUES (4644,'Etobichoker Double Belgian IPA','Pale American-Belgo-Style Ale','Легенда гласит, что Этобичокер поднялся из глубин Хамбера, терроризируя жителей Этобичокера и преследуя их детей во сне. Переутомленные граждане повсюду, от Шервей Гарденс до Лонгбранча, до сих пор ежедневно сообщают о том, что видели отвратительного монстра. 
INSERT INTO "beers" VALUES (4645,'Etoile du Nord','Golden or Blonde Ale','Первоначальное имя «Les Frères de la Bière», это фруктовый партнер с английским пивоваром Джона Дэвидсона из пивоварни Swale, в кадрах проекта Interreg.
INSERT INTO "beers" VALUES (4646,'Etrusca','Herb and Spice Beer','Созданный совместно с нашими друзьями Сэмом Каладжионе и доктором Пэтом Макговерном из Штатов и основателем Baladin Тео Муссо, это действительно нечто уникальное: поскольку мы использовали необычные ингредиенты (для пива) и, прежде всего, необычную систему ферментации (амфоры!), в результате получилась настоящая «археологическая находка». Янтарный цвет напоминает выдержанные в амфорах вина; в аромате ощущаются ноты меда и ягод, а также довольно необычные минеральные ноты. Когда вы пробуете его, резкость является главной чертой, а за медовыми нотами быстро следуют минеральные, снова. Так что те немногие, кто получит возможность попробовать это чрезвычайно редкое пиво (которое, конечно, было произведено в очень ограниченном количестве), смогут сказать, что он сделал глоток истории!','https://brewerydb-images.s3.amazonaws.com/beer/AFT4jW/upload_5iwl5p-medium.png',9.3,5,1.03,1.006,0,27,1);
INSERT INTO "beers" VALUES (4647,'Eucharius Märzen','American-Style Märzen / Oktoberfest','Красно-золотое Märzenbier, как диктует традиция. Пряное в бочке со сбалансированным хмелевым вкусом. Замок-ремешок на этой пивной классике пробуждает воспоминания о былых временах.','https://brewerydb-images.s3.amazonaws.com/beer/lLWx7s/upload_DWsVcl-medium.png',4.9,20,1.05,1.012,0,16,1);
INSERT INTO "beers" VALUES (4648,'Euchre Pils','German-Style Pilsener','','https://brewerydb-images.s3.amazonaws.com/beer/KIY8Je/upload_h8EEbF-medium.png',5.7,25,1.044,1.006,0,16,1);
INSERT INTO "beers" VALUES (4649,'Euforia Toffee Nut','English-Style Brown Ale','Настоящий оригинал DuClaw Brewing Company, этот стильный вариант английского коричневого эля темно-коричневый, средней плотности, сливочный и невероятно ароматный! EuForia сочетает жареный солод с изысканным кофе, чтобы создать ликующий водоворот карамельных, миндальных и ирисовых вкусов, которые приведут ваши нёба в состояние экстаза. Впервые сваренный в 2006 году, этот мягкий, очень питкий 5% abv жидкий блаженство в бутылке/пинте доступен круглый год, так чего же вы ждете? Давайте, будьте счастливы!','https://brewerydb-images.s3.amazonaws.com/beer/o4ENUL/upload_NtQqji-medium.png',5,15,1.04,1.008,0,15,1);
INSERT INTO "beers" VALUES (4650,'Eugene Collaboration English-Style IPA','English-Style India Pale Ale','Это интерпретация английского IPA от Ninkasi. Английские IPA — это хмелевые, умеренно крепкие светлые эли с более солодовым характером, чем их американские собратья IPA. Они обычно демонстрируют хмелевые ароматы и вкусы, которые являются цветочными, землистыми и слегка травяными, подкрепленные солодом, который имеет хлебный, бисквитный, поджаренный, ирисковый или карамельный оттенок. Мы сварили наше пиво с использованием хмеля Maris Otter & 2-Row Pale Malt и хмеля East Kent & Styrian Golding. Наслаждайтесь!','https://brewerydb-images.s3.amazonaws.com/beer/6zB42I/upload_tIxnbd-medium.png',6,35,1.05,1.012,0,21,1);
INSERT INTO "beers" VALUES (4651,'Eugene Porter','Robust Porter','Яркий, крепкий портер, полный тепла и шоколадного солода. Eugene — крепкий портер, названный в честь Юджина В. Дебса, американского профсоюзного лидера и активиста, который возглавил забастовку на железной дороге Пульмана в 1894 году. Ассортимент бельгийских специальных солодов образует сложную структуру поджаренного зерна и карамельных вкусов. Темный шоколадный солод делает этот портер черным как ночь и придает ему его отчетливую интенсивную, шоколадную сущность.','https://brewerydb-images.s3.amazonaws.com/beer/J0Q0za/upload_GenElG-medium.png',6.8,25,1.045,1.008,0,19,1);
INSERT INTO "beers" VALUES (4652,'Euphoria Pale Ale','American-Style Pale Ale','Это сезонное пиво сварено в духе «сделай сам» (DIY) вместе с нашими друзьями из Бейфилда на Venture Snowboard Factory. Индийский светлый эль Euphoria производится с использованием большого количества хмеля Golding для придания уникального пряного послевкусия. Отлично подходит для катания на склонах. 
INSERT INTO "beers" VALUES (4653,'Euphoric Dreams','American-Style Sour Ale','Кислый NEIPA смешанного брожения, сваренный из овса, мягкой белой пшеницы, пшеничных хлопьев, двухрядного ячменя и молочного сахара с щедрым добавлением вирпула хмеля Michigan Copper и Motueka. DDH с Galaxy, Citra и Motueka, а затем выдержанный с зеленым чаем и манго.','https://brewerydb-images.s3.amazonaws.com/beer/hl93XI/upload_EuI8XX-medium.png',7.1,NULL,NULL,NULL,0,24,1);
INSERT INTO "beers" VALUES (4654,'Eureka!','Belgian-Style White (or Wit) / Belgian-Style Wheat','','https://brewerydb-images.s3.amazonaws.com/beer/66YIzN/upload_pRtLrx-medium.png',5.5,10,1.044,1.006,0,9,1);
INSERT INTO "beers" VALUES (4655,'EurekaSeoul Hop Ale','American-Style India Pale Ale','Безумно мутный и дымчатый. Спелый, сочный фруктовый аромат взрывается в носу, а пикантный цитрусовый и свежий вкус танцуют на языке. Он сбалансирован с мягким ощущением во рту, что делает его чрезвычайно питким.','https://brewerydb-images.s3.amazonaws.com/beer/jRQJcp/upload_vcoJnN-medium.png',6.5,50,1.06,1.012,0,13,1);
INSERT INTO "beers" VALUES (4656,'Euro Skinny Jeans','American-Style Pale Ale','','https://brewerydb-images.s3.amazonaws.com/beer/80LYCv/upload_tcYefg-medium.png',4,30,1.044,1.008,0,14,1);
INSERT INTO "beers" VALUES (4657,'Eurotrash Pilz','Specialty Beer','Пильзнер Лагер','https://brewerydb-images.s3.amazonaws.com/beer/cS3ZQi/upload_TWovzg-medium.png',5,NULL,1.03,1.006,0,29,1);
INSERT INTO "beers" VALUES (4658,'Eurotrip','American-Style Brown Ale','Этот Southern London Brown Ale возвращается из мертвых. Это более сладкий и низкотемпературный родственник вашего стандартного English Brown Ale. Наслаждайтесь!','https://brewerydb-images.s3.amazonaws.com/beer/ECbRAy/upload_7ePfhF-medium.png',5.7,25,1.04,1.01,0,28,1);
INSERT INTO "beers" VALUES (4659,'Eva','Specialty Beer','','https://brewerydb-images.s3.amazonaws.com/beer/lG6C3T/upload_uiCn0p-medium.png',0,NULL,1.03,1.006,0,7,1);
INSERT INTO "beers" VALUES (4660,'EVA Cöttbusser','Specialty Beer','Eva Cöttbusser — это старый немецкий стиль, который не представлен ни одной известной нам пивоварней в Германии, а в Северной Америке — только одной. В Германии его больше не варят, потому что он вне закона. При изготовлении пива запрещено использовать что-либо, кроме солода, хмеля, дрожжей и воды.','https://brewerydb-images.s3.amazonaws.com/beer/19VFRu/upload_W9UxUO-medium.png',7.2,NULL,1.03,1.006,0,9,1);
