INSERT INTO "beers" VALUES (4662,'Evan John Porter','Brown Porter','','https://brewerydb-images.s3.amazonaws.com/beer/NZniWI/upload_cD5tCS-medium.png',4.7,20,1.04,1.006,0,10,1);
INSERT INTO "beers" VALUES (4663,'Evander','American-Style Brown Ale','','https://brewerydb-images.s3.amazonaws.com/beer/ZNRXb7/upload_Y9k4Kz-medium.png',5.5,25,1.04,1.01,0,14,1);
INSERT INTO "beers" VALUES (4664,'Evander','American-Style Pale Ale','Художник и всесторонне действующий человек эпохи Возрождения Эвандер прокладывает путь уникальности в мире однообразия. Приглашаем вас посетить его дом и студию в Пасс-а-Гриль, Флорида, и насладиться его пивом почаще!','https://brewerydb-images.s3.amazonaws.com/beer/MeKoKL/upload_ZAzYcf-medium.png',4.7,30,1.044,1.008,0,30,1);
