INSERT INTO "beers" VALUES (4666,'Evans Dark Lager','American-Style Dark Lager','','https://brewerydb-images.s3.amazonaws.com/beer/Qm3l21/upload_JyQft3-medium.png',0,14,1.04,1.008,0,19,1);
INSERT INTO "beers" VALUES (4667,'Evans Extra Lager Beer','American-Style Premium Lager','','https://brewerydb-images.s3.amazonaws.com/beer/vnUTRq/upload_PrYvs5-medium.png',0,6,1.044,1.01,0,19,1);
