INSERT INTO "beers" VALUES (5427,'FLECKS Rot','German-Style Altbier','Характеризующееся карамельным солодом по цвету и вкусу, сладость этого пива вдохновит и удивит вас. Нотки фруктов и ягод делают его невероятно вкусным и непреодолимым.','https://brewerydb-images.s3.amazonaws.com/beer/9zehFK/upload_ZZyFEf-medium.png',0,25,1.044,1.008,0,21,1);
