INSERT INTO "beers" VALUES (5531,'Flying Circus','South German-Style Hefeweizen / Hefeweissbier','Это пиво немецкого происхождения, мутное на вид, бледного цвета. Нотка кокоса и бананового вкуса вместе с сильной карбонизацией делают это веселое пиво идеальным для солнечного дня.','https://brewerydb-images.s3.amazonaws.com/beer/sd70Zj/upload_r8Ha3g-medium.png',4.7,10,1.047,1.008,0,20,1);
