INSERT INTO "beers" VALUES (5660,'Fogust Wheat Ale','South German-Style Hefeweizen / Hefeweissbier','Хефевайцен • Мутный, соломенный цвет • Канадская пшеница • Тонкие нотки банана и гвоздики • Чистое, мягкое послевкусие • Летнее пиво, по желанию солнце','https://brewerydb-images.s3.amazonaws.com/beer/qu0t1m/upload_4MQ6jn-medium.png',4.5,10,1.047,1.008,0,26,1);
