INSERT INTO "beers" VALUES (5673,'For Great Justice','Brown Porter','Прекрасный пьющийся коричневый портер, настоянный на поджаренном на дровах кокосе.','https://brewerydb-images.s3.amazonaws.com/beer/H9LCnO/upload_l20bMy-medium.png',4.5,20,1.04,1.006,0,5,1);
